import React, { useState, useEffect, useCallback } from "react";
import { useNavigate, useParams } from "react-router-dom";

const ChoseType = () => {
  const [type, setType] = useState("");
  const params = useParams();

  const navigate = useNavigate();

  const commitChange = useCallback(async () => {
    // update userType
    const userid = params.userid;
    console.log(userid);
    console.log(type);
    try {
      const response = await fetch(
        `http://localhost:3500/api/users/${userid}/${type}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      if (!response.ok) {
        console.log("user connected fails");
        //setConnectedFails(true);
      } else {
        if (response.status === 200) {
          const { user, newRecordId } = await response.json();
          console.log("Response is ok", user, newRecordId);
          console.log(user);
          console.log(response);
          if (user) {
            //create user with full data in localStoarge
            localStorage.setItem("connected_user", JSON.stringify(user));
            //In case of exist user
            console.log("naviget home-page");
            console.log(user.userID);
            if (user.userType !== "") {
              navigate(`/${user.user_type}/${user.user_id}`);
            } else {
              console.log(
                "some errore occurred when user update in DB (userType did not updata as expected)"
              );
            }
          } else {
            console.log("user is null");
          }
        }
      }
    } catch (error) {
      console.log("ERROR", error);
    }
  }, [params, type, navigate]);

  useEffect(() => {
    console.log("useEffect in choseType");
    if (type) commitChange();
  }, [type, commitChange]);

  return (
    <div>
      <button className="add-btn new-btn"
        onClick={() => {
          setType("student");
        }}
      >
        Student
      </button>
      <button className="add-btn new-btn"
        onClick={() => {
          setType("teacher");
        }}
      >
        Teacher
      </button>
    </div>
  );
};

export default ChoseType;
