import React, { useState, useEffect } from "react";
import LessonOptionsList from "./Search/LessonOptionsList";

const SearchFilters = () => {
  const [lessonOptions, setLessoneOptions] = useState(null);
  const [filteredLessons, setFilteredLessons] = useState(null);

  const subjects = [
    "Math",
    "English",
    "Computer Science",
    "Physics",
    "Science",
  ];

  const [searchCriteria, setSearchCriteria] = useState({
    subject: [],
    zoom: false,
    frontal: false,
    start_time: currentDate,
    end_time: endDate,
    subject_type: "High School",
    units: 3,
  });

  const [sortCriteria, setSortCriteria] = useState("date");
  const [sortDirection, setSortDirection] = useState("asc"); // 'asc' for ascending, 'desc' for descending

  const getSortedLessones = (lessones) => {
    return [...lessones].sort((a, b) => {
      let comparison = 0;
      switch (sortCriteria) {
        case "teacher_name":
          comparison = a.teacher_name.localeCompare(b.teacher_name);
          break;
        case "lesson_cost":
          comparison = a.lesson_cost - b.lesson_cost;
          break;
        case "date":
          comparison = new Date(a.start_time) - new Date(b.start_time);
          break;

        default:
          break;
      }

      return sortDirection === "desc" ? -comparison : comparison;
    });
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setSearchCriteria((prev) => ({ ...prev, [name]: value }));
  };

  const getTeachersLessonOptions = async () => {
    try {
      const response = await fetch(
        "https://privatelessonsserver.azurewebsites.net/api/timeAbilities",
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      if (response.ok) {
        const responseData = await response.json();
        if (responseData.length > 0) {
          // Use length to check if the array is not empty
          setLessoneOptions([...responseData]);
        }
      } else {
        setLessoneOptions(null);
      }
    } catch (error) {
      // console.log("Error fetching data:", error);
    }
  };

  const handleSearch = () => {
    const criteriaStartDate = new Date(searchCriteria.start_time);
    const criteriaEndDate = new Date(searchCriteria.end_time);

    if (lessonOptions) {
      const filtered = lessonOptions.filter((lesson) => {
        if (lesson.subject_type !== searchCriteria.subject_type) return false;
        if (
          searchCriteria.teacher_name &&
          searchCriteria.teacher_name.length > 0 &&
          !lesson.teacher_name
            .toLowerCase()
            .includes(searchCriteria.teacher_name.toLowerCase())
        )
          return false;
        if (
          searchCriteria.subject.length > 0 &&
          !searchCriteria.subject.includes(lesson.subject)
        )
          return false;

        if (
          searchCriteria.lesson_cost &&
          parseFloat(lesson.lesson_cost) > searchCriteria.lesson_cost
        )
          return false;
        const lessonStartTime = new Date(lesson.start_time);
        const lessonEndTime = new Date(lesson.end_time);
        if (
          lessonStartTime < criteriaStartDate ||
          lessonEndTime > criteriaEndDate
        ) {
          return false;
        }

        if (
          (searchCriteria.zoom && !lesson.zoom) ||
          (searchCriteria.frontal && !lesson.frontal)
        )
          return false;

        if (Number(searchCriteria.units) > lesson.units) return false;
        return true;
      });
      // Set search list in the filteredList
      setFilteredLessons(getSortedLessones(filtered));
    }
  };

  useEffect(() => {
    if (lessonOptions !== null) {
      handleSearch();
    }
  }, [lessonOptions, searchCriteria, sortCriteria, sortDirection]);

  useEffect(() => {
    if (lessonOptions === null) {
      getTeachersLessonOptions();
    }
  }, []);

  return (
    <div>
      <div>
        <div>
          <strong>Educational Level:</strong>
          <select
            name="subject_type"
            value={searchCriteria.subject_type}
            onChange={handleInputChange}
          >
            <option value="School">School</option>
            <option value="High School">High School</option>
            <option value="University">University</option>
          </select>
        </div>
        <div>
          <strong>Subjects:</strong>
          <div>
            {subjects.map((sub) => (
              <div key={sub}>
                <label>
                  <input
                    type="checkbox"
                    name="subject"
                    value={sub}
                    checked={searchCriteria.subject.includes(sub)}
                    onChange={(e) => {
                      if (e.target.checked) {
                        setSearchCriteria((prev) => ({
                          ...prev,
                          subject: [...prev.subject, sub],
                        }));
                      } else {
                        setSearchCriteria((prev) => ({
                          ...prev,
                          subject: prev.subject.filter((s) => s !== sub),
                        }));
                      }
                    }}
                  />
                </label>
              </div>
            ))}
          </div>
        </div>
        <div>
          <strong>Units:</strong>
          <input
            type="number"
            name="units"
            value={searchCriteria.units}
            min={0}
            onChange={handleInputChange}
          ></input>
        </div>

        <div>
          <div>
            <label>
              <strong>from:</strong>
            </label>
          </div>
          <div>
            <input
              type="datetime-local"
              min={currentDate}
              value={searchCriteria.start_time}
              onChange={(e) =>
                setSearchCriteria((prev) => ({
                  ...prev,
                  start_time: e.target.value,
                }))
              }
            />
          </div>
          <div>
            <label>
              <strong>To:</strong>
            </label>
          </div>
          <div>
            <input
              type="datetime-local"
              min={currentDate}
              value={searchCriteria.end_time}
              onChange={(e) =>
                setSearchCriteria((prev) => ({
                  ...prev,
                  end_time: e.target.value,
                }))
              }
            />
          </div>
        </div>
        <div>
          <label>
            <strong> Teacher Name:</strong>
          </label>
          <input
            type="text"
            name="teacher_name"
            value={searchCriteria.teacher_name}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>
            <strong>Maximum Price</strong>
          </label>
          <input
            type="number"
            name="price"
            min={0}
            value={searchCriteria.lesson_cost}
            onChange={(e) => {
              const { value } = e.target;
              const numericValue = parseFloat(value);
              setSearchCriteria((prev) => ({
                ...prev,
                lesson_cost: numericValue,
              }));
            }}
          />
        </div>
        <div>
          <label>
            <strong>Sort by:</strong>
          </label>
          <div>
            <select
              value={sortCriteria}
              onChange={(e) => setSortCriteria(e.target.value)}
            >
              <option value="date">Date</option>
              <option value="teacher_name">Name</option>
              <option value="lesson_cost">Cost</option>
            </select>
            <select
              value={sortDirection}
              onChange={(e) => setSortDirection(e.target.value)}
            >
              <option value="asc">Low to High</option>
              <option value="desc">High to Low</option>
            </select>
          </div>
        </div>
        <div>
          <button onClick={handleSearch}>Filter</button>
        </div>
      </div>
      {filteredLessons !== null && (
        <LessonOptionsList
          lessonOptions={filteredLessons}
          onRegister={handleRegister}
        />
      )}
    </div>
  );
};
export default SearchFilters;
