import React, { useState, useEffect } from "react";
import { useWebSocket } from "./WebSocketContext";

const TeacherList = ({ lessonOptions, onRegister }) => (
  <div>
    {lessonOptions ? (
      <ul>
        {lessonOptions.map((lessonOption) => (
          <div key={lessonOption.time_ability_id}>
            <LessonItem lessonOption={lessonOption} onRegister={onRegister} />
            <br />
          </div>
        ))}
      </ul>
    ) : (
      <p>No Lessons found for the given criteria.</p>
    )}
  </div>
);
function formatDate(dateString) {
  const date = new Date(dateString);

  const day = String(date.getDate()).padStart(2, "0");
  const month = String(date.getMonth() + 1).padStart(2, "0"); // Months are 0-based
  const hours = String(date.getHours()).padStart(2, "0");
  const minutes = String(date.getMinutes()).padStart(2, "0");

  return `${day}/${month} ${hours}:${minutes}`;
}

const LessonItem = ({ lessonOption, onRegister }) => (
  <div>
    <strong>Name:</strong> {lessonOption.teacher_name}
    <br />
    <strong>Subject:</strong> {lessonOption.subject}
    <br />
    <strong>Price:</strong> â‚ª{lessonOption.lesson_cost}
    <br />
    <strong>Method:</strong> {lessonOption.subject_type}
    <br />
    <strong>Units:</strong> {lessonOption.units}
    <br />
    {/* <label>
      <input type="checkbox" checked={lessonOption.zoom} readOnly />
      Zoom
    </label>
    <label>
      <input type="checkbox" checked={lessonOption.frontal} readOnly />
      Frontal
    </label>
    <br /> */}
    <strong>Time:</strong> {formatDate(lessonOption.start_time)} -{" "}
    {formatDate(lessonOption.end_time)}
    <br />
    {lessonOption.frontal === 1 && (
      <>
        <strong>Location:</strong> {lessonOption.address}
        <br />
      </>
    )}
    <strong>Email:</strong> {lessonOption.email}
    <br />
    <strong>Phone:</strong> {lessonOption.phone}
    <br />
    <button className="button-sender" onClick={() => onRegister(lessonOption)}>
      Register the lesson
    </button>
    <br />
  </div>
);

const TeacherSearch = () => {
  const { socket, registerMessageCallback } = useWebSocket();

  const [lessonOptions, setLessoneOptions] = useState(null);
  const [filteredLessons, setFilteredLessons] = useState(null);

  let currentDate = new Date();
  currentDate = currentDate.toISOString().split("T")[0] + "T" + currentDate.toTimeString().slice(0, 5);
  let endDate = new Date();
  endDate.setDate(new Date().getDate() + 14);
  endDate = endDate.toISOString().split("T")[0] +"T" + endDate.toTimeString().slice(0, 5);
  const [searchCriteria, setSearchCriteria] = useState({
    subject: [],
    zoom: false,
    frontal: false,
    start_time:
      currentDate,
    end_time:
      endDate,
    subject_type: "High School",
    units: 3,
  });

  const [sortCriteria, setSortCriteria] = useState("date");
  const [sortDirection, setSortDirection] = useState("asc"); // 'asc' for ascending, 'desc' for descending



  const getTeachersLessonOptions = async () => {
    console.log("Trying to get lessonsOptions from the server");
    try {
      const response = await fetch("http://localhost:3500/api/timeAbilities", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        console.log("response is ok");
        const responseData = await response.json();
        console.log(responseData.length > 0);
        if (responseData.length > 0) {
          // Use length to check if the array is not empty
          console.log("Setting LessoneOptions");
          setLessoneOptions([...responseData]);
          console.log(responseData);
        }
      } else {
        setLessoneOptions(null);
      }
    } catch (error) {
      console.log("Error fetching data:", error);
    }
  };

  const updateLessonOptions = (data) => {
    const { updateType } = data;
    console.log(updateType);
    if (updateType === "lessons") {
      console.log("update list of lessonOptions");
      getTeachersLessonOptions();
    }
  };

  useEffect(() => {
    if (lessonOptions !== null) {
      console.log("useEffect lessonOptions");
      handleSearch();
    }
  }, [lessonOptions, searchCriteria, sortCriteria, sortDirection]);

  useEffect(() => {
    console.log("useEffect filteredLessons");
  }, [filteredLessons]);

  useEffect(() => {
    console.log("useEffect in TecherSearch");
    let cleanup;
    if (lessonOptions === null) {
      console.log("register MessageCallbacks");
      cleanup = registerMessageCallback(updateLessonOptions);
      console.log("Calling getTeachersLessonOptions");
      getTeachersLessonOptions();
    }
    return () => {
      //console.log("cleanup");
      //cleanup(); // Unregister the callback when the component unmounts
    };
  }, []);

  const handleRegister = async (lessonAbility) => {
    const user = JSON.parse(localStorage.getItem("connected_user"));
    console.log(user);
    if (user) {
      try {
        // Make POST request to create a lesson
        const response = await fetch("http://localhost:3500/api/lessons", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            user_id: user.user_id,
            time_ability_id: lessonAbility.time_ability_id,
            paid_up: false,
            status: "Pending",
          }),
        });

        // Check if the request was successful
        if (!response.ok) {
          const responseData = await response.json();
          throw new Error(responseData.error || "Failed to create lesson");
        } else {
          // change after
          console.log("create lesson");
          // alert("Register Successfull for the lesson.");
          // show alert
          openAlert(true);
          setFilteredLessons((prevLessons) =>
            prevLessons.filter(
              (l) => l.time_ability_id !== lessonAbility.time_ability_id
            )
          );
        }
      } catch (error) {
        console.error("Error fetching data:", error);
        openAlert(false);
      }
    } else {
      console.log("can't find connected user to the website");
    }
  };

  const getSortedLessones = (lessones) => {
    return [...lessones].sort((a, b) => {
      let comparison = 0;
      switch (sortCriteria) {
        case "teacher_name":
          comparison = a.teacher_name.localeCompare(b.teacher_name);
          break;
        case "lesson_cost":
          comparison = a.lesson_cost - b.lesson_cost;
          break;
        case "date":
          comparison = new Date(a.start_time) - new Date(b.start_time);
          break;

        default:
          break;
      }

      return sortDirection === "desc" ? -comparison : comparison;
    });
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setSearchCriteria((prev) => ({ ...prev, [name]: value }));
  };

  const handleSearch = () => {
    console.log(searchCriteria);

    const criteriaStartDate = new Date(searchCriteria.start_time);
    const criteriaEndDate = new Date(searchCriteria.end_time);

    if (lessonOptions) {
      const filtered = lessonOptions.filter((lesson) => {
        if (lesson.subject_type !== searchCriteria.subject_type) return false;
        if (
          searchCriteria.teacher_name &&
          searchCriteria.teacher_name.length > 0 &&
          !lesson.teacher_name
            .toLowerCase()
            .includes(searchCriteria.teacher_name.toLowerCase())
        )
          return false;
        if (
          searchCriteria.subject.length > 0 &&
          !searchCriteria.subject.includes(lesson.subject)
        )
          return false;

        if (
          searchCriteria.lesson_cost &&
          parseFloat(lesson.lesson_cost) > searchCriteria.lesson_cost
        )
          return false;
        const lessonStartTime = new Date(lesson.start_time);
        const lessonEndTime = new Date(lesson.end_time);
        if (
          lessonStartTime < criteriaStartDate ||
          lessonEndTime > criteriaEndDate
        ) {
          return false;
        }

        if (
          (searchCriteria.zoom && !lesson.zoom) ||
          (searchCriteria.frontal && !lesson.frontal)
        )
          return false;


        if (Number(searchCriteria.units) > lesson.units) return false;
        return true;
      });
      console.log("filtered list:", filtered);
      // Set search list in the filteredList
      setFilteredLessons(getSortedLessones(filtered));
    }
  };

  const subjects = ["Math", "English", "Computer Science", "Physics", "Science"];

  const [isVisible, setIsVisible] = useState(true);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(false);

  const openAlert = (isSuccess) => {
    setSuccess(isSuccess);
    setError(!isSuccess);
    setIsVisible(true);
    setTimeout(() => {
      setIsVisible(false);
    }, 6000);
  };

  return (
    <div>
      <div className="message-container">
        {isVisible && success && (
          <div className="alert-container">
            <div id="success-box">
              <div className="dot"></div>
              <div className="dot two"></div>
              <div className="face">
                <div className="eye"></div>
                <div className="eye right"></div>
                <div className="mouth happy"></div>
              </div>
              <div className="shadow scale"></div>
              <div className="message">
                <h1 className="alert alert_title">Success!</h1>
                <p className="alert_per">yay, everything is working.</p>
              </div>
            </div>
          </div>
        )}

        {isVisible && error && (
          <div>
            <div className="alert-container">
              <div id="error-box">
                <div className="dot"></div>
                <div className="dot two"></div>
                <div className="face">
                  <div className="eye"></div>
                  <div className="eye right"></div>
                  <div className="mouth sad"></div>
                </div>
                <div className="shadow move"></div>
                <div className="message">
                  <h1 className="alert alert_title">Error!</h1>
                  <p className="alert_per">oh no, something went wrong.</p>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
      <div className="search-container">
        <h1 className="details-header">Search a Lesson</h1>

        <div className="search-card">
          <div>
            <strong className="lable-text">Educational Level:</strong>
            <select
              className="custom-dropdown"
              name="subject_type"
              value={searchCriteria.subject_type}
              onChange={handleInputChange}
            >
              <option value="School">School</option>
              <option value="High School">High School</option>
              <option value="University">University</option>
            </select>
          </div>

          <div>
            <strong>Subjects:</strong>
            <div className="checkbox-container">
              {subjects.map((sub) => (
                <div key={sub} className="checkbox-item">
                  <label className="checkbox">
                    <input
                      type="checkbox"
                      name="subject"
                      value={sub}
                      checked={searchCriteria.subject.includes(sub)}
                      onChange={(e) => {
                        if (e.target.checked) {
                          setSearchCriteria((prev) => ({
                            ...prev,
                            subject: [...prev.subject, sub],
                          }));
                        } else {
                          setSearchCriteria((prev) => ({
                            ...prev,
                            subject: prev.subject.filter((s) => s !== sub),
                          }));
                        }
                      }}
                    />
                    <svg viewBox="0 0 21 18">
                      <symbol
                        id="tick-path"
                        viewBox="0 0 21 18"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          d="M5.22003 7.26C5.72003 7.76 7.57 9.7 8.67 11.45C12.2 6.05 15.65 3.5 19.19 1.69"
                          fill="none"
                          strokeWidth="2.25"
                          stroke-linecap="round"
                          stroke-linejoin="round"
                        />
                      </symbol>
                      <defs>
                        <mask id="tick">
                          <use class="tick mask" href="#tick-path" />
                        </mask>
                      </defs>
                      <use class="tick" href="#tick-path" stroke="currentColor" />
                      <path
                        fill="white"
                        mask="url(#tick)"
                        d="M18 9C18 10.4464 17.9036 11.8929 17.7589 13.1464C17.5179 15.6054 15.6054 17.5179 13.1625 17.7589C11.8929 17.9036 10.4464 18 9 18C7.55357 18 6.10714 17.9036 4.85357 17.7589C2.39464 17.5179 0.498214 15.6054 0.241071 13.1464C0.0964286 11.8929 0 10.4464 0 9C0 7.55357 0.0964286 6.10714 0.241071 4.8375C0.498214 2.39464 2.39464 0.482143 4.85357 0.241071C6.10714 0.0964286 7.55357 0 9 0C10.4464 0 11.8929 0.0964286 13.1625 0.241071C15.6054 0.482143 17.5179 2.39464 17.7589 4.8375C17.9036 6.10714 18 7.55357 18 9Z"
                      />
                    </svg>
                    <svg class="lines" viewBox="0 0 11 11">
                      <path d="M5.88086 5.89441L9.53504 4.26746" />
                      <path d="M5.5274 8.78838L9.45391 9.55161" />
                      <path d="M3.49371 4.22065L5.55387 0.79198" />
                    </svg>
                    <strong style={{ marginLeft: "25px" }}>{sub}</strong>
                  </label>
                </div>
              ))}
            </div>

          </div>
          <div>
            <strong className="lable-text">Units:</strong>
            <input
              className="text-input"
              type="number"
              name="units"
              value={searchCriteria.units}
