// WebSocketContext.js

import React, { createContext, useContext, useEffect, useState } from "react";

const WebSocketContext = createContext();

export const useWebSocket = () => useContext(WebSocketContext);

export const WebSocketProvider = ({ children }) => {
  const [socket, setSocket] = useState(null);
  const messageCallbacks = new Set();

  useEffect(() => {
    const newSocket = new WebSocket("ws://localhost:3500");

    newSocket.addEventListener("open", (event) => {
      // console.log("WebSocket connection opened.");
    });

    newSocket.addEventListener("message", (event) => {
      // Call all registered message callbacks
      // console.log("revived message");
      // console.log(messageCallbacks);
      messageCallbacks.forEach((callback) => callback(JSON.parse(event.data)));
    });

    setSocket(newSocket);

    return () => {
      newSocket.close();
    };
  }, []);

  const registerMessageCallback = (callback) => {
    // console.log("add callback to webSocket");
    messageCallbacks.add(callback);
    console.log(callback);
    console.log(messageCallbacks);

    return () => {
      console.log("delete callback from webSocket");
      //messageCallbacks.delete(callback);
    };
  };

  return (
    <WebSocketContext.Provider value={{ socket, registerMessageCallback }}>
      {children}
    </WebSocketContext.Provider>
  );
};
