import React, { useState, useEffect } from "react";
import NewAbilityModal from "./NewAbilityModal";
const TeacherAbilities = ({ userDetails }) => {
  const [abilities, setAbilities] = useState([]);

  const defaultAbility = { subject_name: "", type: "", units: 3, cost: 0 };
  const [showModal, setShowModal] = useState(false);
  const [newAbility, setNewAbility] = useState(defaultAbility);
  const [editingIndex, setEditingIndex] = useState(-1); // -1 means adding a new subject
  const [user] = useState(userDetails);


  const URL = "https://privatelessonsserver.azurewebsites.net/api/abilities/";
  const subjectsList = ["Math", "Science", "History", "Literature", "Music"];

  useEffect(() => {
    // Fetch teacher abilities from the server
    fetch(`${URL}${user.user_id}`)
      .then((response) => response.json())
      .then((data) => {
        setAbilities(data); // Update the state with the fetched data
      })
      .catch((error) => {
        // console.error("Error fetching teacher abilities:", error);
      });
  }, []);

  const saveAbility = async () => {
    if (editingIndex === -1) {
      fetch(`${URL}${user.user_id}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newAbility),
      })
        .then((response) => {
          if (response.ok) {
            setAbilities((prevabilities) => [...prevabilities, newAbility]);
            setShowModal(false);
          } else {
            // console.error(
            //   "Error adding ability on the server:",
            //   response.statusText
            // );
            // Handle duplicate entry error
            if (response.status === 409) {
              alert("This ability already exists.");
            } else {
              // Handle other server-side errors
              alert(
                "An error occurred while adding the ability. Please try again."
              );
            }
          }
        })
        .catch((error) => {
          // console.error("Fetch error:", error);
          // Handle network error
          alert(
            "A network error occurred. Please check your internet connection and try again."
          );
        });
    } else {
      // Edit existing subject locally
      const updatedAbilities = [...abilities];
      updatedAbilities[editingIndex] = newAbility;
      setAbilities(updatedAbilities);
      if (newAbility.lesson_ability_id) {
        // Send a request to the server to update the ability
        fetch(`${URL}ability/${newAbility.lesson_ability_id}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(newAbility),
        })
          .then((response) => {
            if (response.ok) {
              setShowModal(false);
            } else {
              // console.error(
              //   "Error updating ability on the server:",
              //   response.statusText
              // );
              // Handle error by showing feedback to the user or reverting changes in UI
              alert(
                "An error occurred while updating the ability. Please try again."
              );
            }
          })
          .catch((error) => {
            // console.error("Fetch error:", error);
            // Handle network error
            alert(
              "A network error occurred. Please check your internet connection and try again."
            );
          })
          .finally(() => {
            // Reset states after both successful and failed update
            setNewAbility(defaultAbility);
            setEditingIndex(-1);
            setShowModal(false);
          });
      }
    }
  };

  const deleteAbility = (ability_id, index) => {
    // Send a DELETE request to the server
    fetch(`${URL}ability/${ability_id}`, {
      method: "DELETE",
    })
      .then((response) => {
        if (response.ok) {
          // Remove ability from local state as well
          const newAbilities = [...abilities];
          newAbilities.splice(index, 1);
          setAbilities(newAbilities);
        } else if (response.status === 500) {
          alert("You still have existing lessons that contain this ability");
        } else {
          // console.error(
          //   "Error deleting ability on the server:",
          //   response.statusText
          // );
        }
      })
      .catch((error) => {
        // console.error("Fetch error:", error);
      });
  };

  const openEditModal = (index) => {
    setNewAbility(abilities[index]);
    setEditingIndex(index);
    setShowModal(true);
  };

  return (
    <div>
      <h1>Teacher Abilities</h1>
      <table>
        <thead>
          <tr>
            <th>Subject</th>
            <th>Level</th>
            <th>Units</th>
            <th>Cost</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {abilities.map((ability, index) => {
            return (
              <tr key={index}>
                <td>{ability.subject_name}</td>
                <td>{ability.type}</td>
                <td>{ability.units}</td>
                <td>{ability.cost}</td>
                <td>
                  <button
                    onClick={() => openEditModal(index)}
                  >
                    Edit
                  </button>
                  <button
                    onClick={() =>
                      deleteAbility(ability.lesson_ability_id, index)
                    }
                  >
                    Delete
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
      {/* Button to open the modal */}
      <button onClick={() => setShowModal(true)}>
        Add New Ability
      </button>
      {/* Open NewAbility Modal */}
      {showModal && <NewAbilityModal></NewAbilityModal>}
    </div>
  );
};
export default TeacherAbilities;
