import React from "react";

const LessonItem = ({ lessonOption, onRegister }) => {
    
  return (
    <div>
      <strong>Name:</strong> {lessonOption.teacher_name}
      <br />
      <strong>Subject:</strong> {lessonOption.subject}
      <br />
      <strong>Price:</strong> â‚ª{lessonOption.lesson_cost}
      <br />
      <strong>Method:</strong> {lessonOption.subject_type}
      <br />
      <strong>Units:</strong> {lessonOption.units}
      <br />
      {/* <label>
        <input type="checkbox" checked={lessonOption.zoom} readOnly />
        Zoom
      </label>
      <label>
        <input type="checkbox" checked={lessonOption.frontal} readOnly />
        Frontal
      </label>
      <br /> */}
      <strong>Time:</strong> {formatDate(lessonOption.start_time)} -{" "}
      {formatDate(lessonOption.end_time)}
      <br />
      {lessonOption.frontal === 1 && (
        <>
          <strong>Location:</strong> {lessonOption.address}
          <br />
        </>
      )}
      <strong>Email:</strong> {lessonOption.email}
      <br />
      <strong>Phone:</strong> {lessonOption.phone}
      <br />
      <button onClick={() => onRegister(lessonOption)}>
        Register the lesson
      </button>
      <br />
    </div>
  );
};
export default LessonItem;
