import React from "react";

const NewAbilityModal = () => {
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    const finalValue = name === "units" ? Number(value) : value;
    setNewAbility((prev) => ({ ...prev, [name]: finalValue }));
  };

  const saveAbility = async () => {
    if (editingIndex === -1) {
      fetch(`${URL}${user.user_id}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newAbility),
      })
        .then((response) => {
          if (response.ok) {
            setAbilities((prevabilities) => [...prevabilities, newAbility]);
            setShowModal(false);
          } else {
            // console.error(
            //   "Error adding ability on the server:",
            //   response.statusText
            // );
            // Handle duplicate entry error
            if (response.status === 409) {
              alert("This ability already exists.");
            } else {
              // Handle other server-side errors
              alert(
                "An error occurred while adding the ability. Please try again."
              );
            }
          }
        })
        .catch((error) => {
          // console.error("Fetch error:", error);
          // Handle network error
          alert(
            "A network error occurred. Please check your internet connection and try again."
          );
        });
    } else {
      // Edit existing subject locally
      const updatedAbilities = [...abilities];
      updatedAbilities[editingIndex] = newAbility;
      setAbilities(updatedAbilities);
      if (newAbility.lesson_ability_id) {
        // Send a request to the server to update the ability
        fetch(`${URL}ability/${newAbility.lesson_ability_id}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(newAbility),
        })
          .then((response) => {
            if (response.ok) {
              setShowModal(false);
            } else {
              // console.error(
              //   "Error updating ability on the server:",
              //   response.statusText
              // );
              // Handle error by showing feedback to the user or reverting changes in UI
              alert(
                "An error occurred while updating the ability. Please try again."
              );
            }
          })
          .catch((error) => {
            // console.error("Fetch error:", error);
            // Handle network error
            alert(
              "A network error occurred. Please check your internet connection and try again."
            );
          })
          .finally(() => {
            // Reset states after both successful and failed update
            setNewAbility(defaultAbility);
            setEditingIndex(-1);
            setShowModal(false);
          });
      }
    }
  };

  return (
    <div>
      <div>
        <h2>Ability Details</h2>
        <div>
          <label>
            <select
              name="subject_name"
              value={newAbility.subject_name}
              onChange={handleInputChange}
              disabled={editingIndex !== -1}
            >
              <option value="" disabled>
                Select subject
              </option>
              {subjectsList.map((subject) => (
                <option key={subject} value={subject}>
                  {subject}
                </option>
              ))}
            </select>
          </label>

          <label>
            <select
              name="type"
              value={newAbility.type}
              onChange={handleInputChange}
              disabled={editingIndex !== -1}
            >
              <option value="" disabled>
                Select type
              </option>
              <option value={"School"}>{"School"}</option>
              <option value={"High School"}>{"High School"}</option>
              <option value={"University"}>{"University"}</option>
            </select>
          </label>
        </div>
        <div>
          <label>Units </label>
          <input
            name="units"
            type="number"
            min={0}
            value={newAbility.units}
            onChange={handleInputChange}
            disabled={editingIndex !== -1}
          />
        </div>
        <div>
          <label>Cost </label>
          <input
            name="cost"
            type="number"
            min={0}
            value={newAbility.cost}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <button onClick={saveAbility}>
            {editingIndex === -1 ? "Add Ability" : "Update Ability"}
          </button>
          <button
            onClick={() => {
              setEditingIndex(-1);
              setShowModal(false);
              setnewAbility(defaultAbility);
            }}
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};
export default NewAbilityModal;
