import React, { useState, useEffect } from "react";
import NewAbilityModal from "./NewAbilityModal";
const TeacherAbilities = ({ userDetails }) => {
  const [abilities, setAbilities] = useState([]);

  const defaultAbility = { subject_name: "", type: "", units: 3, cost: 0 };
  const [showModal, setShowModal] = useState(false);
  const [newAbility, setNewAbility] = useState(defaultAbility);
  const [editingIndex, setEditingIndex] = useState(-1); // -1 means adding a new subject
  const [user] = useState(userDetails);


  const URL = "https://privatelessonsserver.azurewebsites.net/api/abilities/";
  const subjectsList = ["Math", "Science", "History", "Literature", "Music"];

  useEffect(() => {
    // Fetch teacher abilities from the server
    fetch(`${URL}${user.user_id}`)
      .then((response) => response.json())
      .then((data) => {
        setAbilities(data); // Update the state with the fetched data
      })
      .catch((error) => {
        // console.error("Error fetching teacher abilities:", error);
      });
  }, []);


  const deleteAbility = (ability_id, index) => {
    // Send a DELETE request to the server
    fetch(`${URL}ability/${ability_id}`, {
      method: "DELETE",
    })
      .then((response) => {
        if (response.ok) {
          // Remove ability from local state as well
          const newAbilities = [...abilities];
          newAbilities.splice(index, 1);
          setAbilities(newAbilities);
        } else if (response.status === 500) {
          alert("You still have existing lessons that contain this ability");
        } else {
          // console.error(
          //   "Error deleting ability on the server:",
          //   response.statusText
          // );
        }
      })
      .catch((error) => {
        // console.error("Fetch error:", error);
      });
  };

  const openEditModal = (index) => {
    setNewAbility(abilities[index]);
    setEditingIndex(index);
    setShowModal(true);
  };

  return (
    <div>
      <h1>Teacher Abilities</h1>
      <table>
        <thead>
          <tr>
            <th>Subject</th>
            <th>Level</th>
            <th>Units</th>
            <th>Cost</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {abilities.map((ability, index) => {
            return (
              <tr key={index}>
                <td>{ability.subject_name}</td>
                <td>{ability.type}</td>
                <td>{ability.units}</td>
                <td>{ability.cost}</td>
                <td>
                  <button
                    onClick={() => openEditModal(index)}
                  >
                    Edit
                  </button>
                  <button
                    onClick={() =>
                      deleteAbility(ability.lesson_ability_id, index)
                    }
                  >
                    Delete
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
      {/* Button to open the modal */}
      <button onClick={() => setShowModal(true)}>
        Add New Ability
      </button>
      {/* Open NewAbility Modal */}
      {showModal && <NewAbilityModal></NewAbilityModal>}
    </div>
  );
};
export default TeacherAbilities;
