import React from "react";

const Details = () => {
  return (
    <div>
      <div>
        <label>Name:</label>
        {isEditing ? (
          <input
            value={user.Name}
            onChange={(e) => updateUser("Name", e.target.value)}
          />
        ) : (
          <span>{user.Name}</span>
        )}
      </div>
      <div>
        <label>Birthday:</label>
        {isEditing ? (
          <input
            type="date"
            value={formatDate(user.Birthdate)}
            onChange={(e) => updateUser("Birthdate", e.target.value)}
          />
        ) : (
          <span>{formatDate(user.Birthdate)}</span>
        )}
      </div>
      <div>
        <label>Phone:</label>
        {isEditing ? (
          <input
            type="tel"
            pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
            value={user.Phone}
            onChange={(e) => updateUser("Phone", e.target.value)}
          />
        ) : (
          <span>{user.Phone}</span>
        )}
      </div>
      <div>
        <label>Email:</label>
        {isEditing ? (
          <input
            type="email"
            value={user.Email}
            onChange={(e) => updateUser("Email", e.target.value)}
          />
        ) : (
          <span>{user.Email}</span>
        )}
      </div>
      <div>
        <label>Address:</label>
        {isEditing ? (
          <input
            value={user.Address}
            onChange={(e) => updateUser("Address", e.target.value)}
          />
        ) : (
          <span>{user.Address}</span>
        )}
      </div>
      <br />
      {!isEditing ? (
        <div>
          <button onClick={() => setIsEditing(true)}>Edit</button>
        </div>
      ) : (
        <div>
          <button
            onClick={() => {
              saveEdit();
            }}
          >
            Save
          </button>
          <button
            onClick={() => {
              setIsEditing(false);
              setUser(userDetails);
            }}
          >
            Cancel
          </button>
        </div>
      )}
    </div>
  );
};
export default Details;
