import React, { useState } from "react";
import UserDetails from "../userDetails/UserDetails";
import Lessons from "../UserLessons/Lessons";
import TeacherSearch from "../Student/Search/TeacherSearch";
import UserCalendar from "../Calendar/UserCalendar";
import TeacherAbilities from "../Teacher/TeacherAbilities";
import { googleLogout } from "@react-oauth/google";
import { useNavigate } from "react-router-dom";
import ResponsiveAppBar from "./ResponsiveAppBar";

const Menu = ({ userDetails }) => {
  const [user, setUser] = useState(userDetails);
  const [activeTab, setActiveTab] = useState(0);
  const navigate = useNavigate();

  const handleUserUpdate = (updatedUser) => {
    setUser(updatedUser);
  };

  const logOut = () => {
    localStorage.removeItem("connected_user");
    googleLogout();
    navigate("/login");
  };

  return (
    <div>
      {/* Tabs navigation */}
      <div>
        {console.log(user.user_type)}
        {user && (
          <ResponsiveAppBar
            userType={user.user_type}
            setActiveTab={setActiveTab}
          ></ResponsiveAppBar>
        )}
      </div>

      {/* Tab content */}
      <div>
        {activeTab === 0 && (
          <UserDetails userDetails={user} onUserUpdate={handleUserUpdate} />
        )}
        {activeTab === 1 && user && user.user_type === "student" && (
          <TeacherSearch userDetails={user}></TeacherSearch>
        )}
        {activeTab === 1 && user && user.user_type === "teacher" && (
          <TeacherAbilities userDetails={user}></TeacherAbilities>
        )}

        {activeTab === 2 && <Lessons userDetails={user} />}
        {activeTab === 3 && <UserCalendar userDetails={user} />}
      </div>
    </div>
  );
};
export default Menu;
