import React, { useState, useEffect } from "react";
import UserDetails from "../UserDetails";
import Lessons from "../Lessons";
import TeacherAbilities from "./TeacherAbilities";
import UserCalendar from "../Calendar/UserCalendar";
import { googleLogout } from "@react-oauth/google";
import { useNavigate } from "react-router-dom";

const TeacherPage = () => {
  const [activeTab, setActiveTab] = useState(0);
  const [user, setUser] = useState(null);
  const [initialLoadComplete, setInitialLoadComplete] = useState(false);
  const navigate = useNavigate();
  
  const handleUserUpdate = (updatedUser) => {
    setUser(updatedUser);
  };

  const initialUser = function () {
    const connected_user = JSON.parse(localStorage.getItem("connected_user"));
    if (connected_user) {
      setInitialLoadComplete(true);
      setUser({
        user_id: connected_user.user_id,
        user_type: connected_user.user_type,
        Name: connected_user.name !== null ? connected_user.name : "",
        Gender: connected_user.gender !== null ? connected_user.gender : "",
        Email: connected_user.email !== null ? connected_user.email : "",
        Address: connected_user.address !== null ? connected_user.address : "",
        Birthdate:
          connected_user.birthdate !== null ? connected_user.birthdate : "",
        Phone: connected_user.phone !== null ? connected_user.phone : "",
      });
    } else {
      // console.log("user initial fails");
    }
  };

  useEffect(() => {
    if (initialLoadComplete === false) {
      initialUser();
    }
  }, []);
  return (
    <div>
      {user && (
        <div >
          <div>
            <button
              className={`item ${activeTab === 0 ? "active" : ""}`}
              onClick={() => setActiveTab(0)}
            >
              Details
            </button>
            <button
              className={`item ${activeTab === 1 ? "active" : ""}`}
              onClick={() => setActiveTab(1)}
            >
              Lessons
            </button>
            <button
              className={`item ${activeTab === 2 ? "active" : ""}`}
              onClick={() => setActiveTab(2)}
            >
              Abilities
            </button>
            <button
              className={`item ${activeTab === 3 ? "active" : ""}`}
              onClick={() => setActiveTab(3)}
            >
              Calendar
            </button>
            <button
              className={`item ${activeTab === 4 ? "active" : ""}`}
              onClick={() => {
                setActiveTab(4);
                localStorage.removeItem("connected_user");
                googleLogout();
                navigate("/login");
              }}
            >
              Logout
            </button>
          </div>
          <div>
            {activeTab === 0 && <UserDetails userDetails={user} onUserUpdate={handleUserUpdate} />}
            {activeTab === 1 && <Lessons userDetails={user} />}
            {activeTab === 2 && <TeacherAbilities userDetails={user} />}
            {activeTab === 3 && <UserCalendar userDetails={user} />}
          </div>
        </div>
      )}
    </div>
  );
};

export default TeacherPage;
