import React, { useEffect, useState } from "react";

const DetailsTable = ({ user, lessons, setLessons }) => {

  const handleLessonStatusUpdate = async (lessonIndex, status, lesson) => {
    // update lesson in server, then ger auto real-time update to the list.
    try {
      const response = await fetch(
        `https://privatelessonsserver.azurewebsites.net/api/lessons/${lesson.lesson_id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ paid_up: lesson.paid_up, status: status }),
        }
      );
      if (!response.ok) {
        // console.log("lesson update fails");
      } else {
        if (response.status === 200) {
          // console.log(`lesson with lesson_id: ${lesson.lesson_id} update`);
        }
      }
    } catch (error) {
      // console.log("ERROR", error);
    }

    // Clone the current lessons array
    const updatedLessons = [...lessons];

    // Update the status of the specific lesson
    updatedLessons[lessonIndex].status = status;

    // Set the updated lessons array as the new state
    setLessons(updatedLessons);
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Subject</th>
            <th>Level</th>
            <th>Units</th>
            <th>Date</th>
            <th>Time</th>
            {/* <th>Paid Up</th> */}
            {/* <th>Frontal</th>
            <th>Zoom</th> */}
            <th>Status</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {lessons &&
            lessons.map((lesson, index) => (
              <tr key={lesson.lesson_id}>
                {user.user_type === "teacher" ? (
                  <td>{lesson.student_name}</td>
                ) : (
                  <td>{lesson.teacher_name}</td>
                )}
                {user.user_type === "teacher" ? (
                  <td>
                    <a href={`tel:${lesson.student_phone}`}>
                      {lesson.student_phone}
                    </a>
                  </td>
                ) : (
                  <td>
                    <a href={`tel:${lesson.teacher_phone}`}>
                      {lesson.teacher_phone}
                    </a>
                  </td>
                )}
                {user.user_type === "teacher" ? (
                  <td>
                    <a href={`mailto:${lesson.student_email}`}>
                      {lesson.student_email}
                    </a>
                  </td>
                ) : (
                  <td>
                    <a href={`mailto:${lesson.teacher_email}`}>
                      {lesson.teacher_email}
                    </a>
                  </td>
                )}
                <td>{lesson.subject}</td>
                <td>{lesson.subject_type}</td>
                <td>{lesson.subject_units}</td>
                <td>{new Date(lesson.b_time).toLocaleDateString()}</td>
                <td>
                  {new Date(lesson.b_time)
                    .toLocaleTimeString()
                    .split(":")
                    .slice(0, 2)
                    .join(":")}
                  -
                  {new Date(lesson.e_time)
                    .toLocaleTimeString()
                    .split(":")
                    .slice(0, 2)
                    .join(":")}
                </td>

                {/* {lesson.paid_up ? <td>V</td> : <td></td>} */}
                {/* {lesson.zoom ? <td>V</td> : <td></td>}
                {lesson.frontal ? <td>V</td> : <td></td>} */}
                <td>{lesson.status}</td>
                {user.user_type === "teacher" &&
                  lesson.status === "Pending" && (
                    <td>
                      <button
                        onClick={() =>
                          handleLessonStatusUpdate(index, "Approved", lesson)
                        }
                      >
                        Approve
                      </button>
                      <button
                        onClick={() =>
                          handleLessonStatusUpdate(index, "Denied", lesson)
                        }
                      >
                        Deny
                      </button>
                    </td>
                  )}
                {(lesson.status === "Approved" ||
                  (lesson.status === "Pending" &&
                    user.user_type === "student")) &&
                  new Date(lesson.b_time) > new Date() && (
                    <td>
                      <button
                        onClick={() =>
                          handleLessonStatusUpdate(index, "Cancelled", lesson)
                        }
                      >
                        Cancel
                      </button>
                    </td>
                  )}
                {lesson.status === "Approved" &&
                  user.user_type !== "student" &&
                  new Date(lesson.e_time) < new Date() && (
                    <td>
                      <button
                        onClick={() =>
                          handleLessonStatusUpdate(index, "Done", lesson)
                        }
                      >
                        Done
                      </button>
                    </td>
                  )}
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
};
export default DetailsTable;
