import React from "react";
import UserDetails from "../userDetails/UserDetails";
import Lessons from "../Lessons";
import TeacherSearch from "./TeacherSearch";
import UserCalendar from "../Calendar/UserCalendar";
import { googleLogout } from "@react-oauth/google";
import { useNavigate } from "react-router-dom";

const Menu = ({ user }) => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState(1);

  const handleUserUpdate = (updatedUser) => {
    setUser(updatedUser);
  };

  return (
    <div>
      {/* Tabs navigation */}
      <div>
        <button onClick={() => setActiveTab(0)}>Details</button>
        <button onClick={() => setActiveTab(1)}>
          {user && user.userType == "student" ? `Search a Lesson` : `Abilitis`}
        </button>
        <button onClick={() => setActiveTab(2)}>Lessons</button>
        <button onClick={() => setActiveTab(3)}>Calendar</button>
        <button
          onClick={() => {
            setActiveTab(4);
            localStorage.removeItem("connected_user");
            googleLogout();
            navigate("/login");
          }}
        >
          Logout
        </button>
      </div>

      {/* Tab content */}
      <div>
        {activeTab === 0 && (
          <UserDetails userDetails={user} onUserUpdate={handleUserUpdate} />
        )}
        {activeTab === 1 && user && user.user_type == "student" ? (
          <TeacherSearch userDetails={user}></TeacherSearch>
        ) : (
          <TeacherSearch userDetails={user}></TeacherSearch>
        )}
        {activeTab === 2 && <Lessons userDetails={user} />}
        {activeTab === 3 && <UserCalendar userDetails={user} />}
      </div>
    </div>
  );
};
export default Menu;
