import React from "react";

const NewAbilityModal = ({ isOpen, onClose, saveAbility }) => {
  const subjectsList = ["Math", "Science", "History", "Literature", "Music"];

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    const finalValue = name === "units" ? Number(value) : value;
    setNewAbility((prev) => ({ ...prev, [name]: finalValue }));
  };



  if (!isOpen) return null;

  return (
    <div>
      <div>
        <h2>Ability Details</h2>
        <div>
          <label>
            <select
              name="subject_name"
              value={newAbility.subject_name}
              onChange={handleInputChange}
              disabled={editingIndex !== -1}
            >
              <option value="" disabled>
                Select subject
              </option>
              {subjectsList.map((subject) => (
                <option key={subject} value={subject}>
                  {subject}
                </option>
              ))}
            </select>
          </label>

          <label>
            <select
              name="type"
              value={newAbility.type}
              onChange={handleInputChange}
              disabled={editingIndex !== -1}
            >
              <option value="" disabled>
                Select type
              </option>
              <option value={"School"}>{"School"}</option>
              <option value={"High School"}>{"High School"}</option>
              <option value={"University"}>{"University"}</option>
            </select>
          </label>
        </div>
        <div>
          <label>Units </label>
          <input
            name="units"
            type="number"
            min={0}
            value={newAbility.units}
            onChange={handleInputChange}
            disabled={editingIndex !== -1}
          />
        </div>
        <div>
          <label>Cost </label>
          <input
            name="cost"
            type="number"
            min={0}
            value={newAbility.cost}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <button onClick={saveAbility}>
            {editingIndex === -1 ? "Add Ability" : "Update Ability"}
          </button>
          <button
            onClick={() => {
                onClose()
              setnewAbility(defaultAbility);
            }}
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};
export default NewAbilityModal;
