import React, { useState, useEffect } from "react";
const TeacherAbilities = ({ userDetails }) => {
  const [abilities, setAbilities] = useState([]);

  const defaultAbility = { subject_name: "", type: "", units: 3, cost: 0 };
  const [showModal, setShowModal] = useState(false);
  const [newAbility, setnewAbility] = useState(defaultAbility);
  const [editingIndex, setEditingIndex] = useState(-1); // -1 means adding a new subject
  const [user] = useState(userDetails);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    const finalValue = name === "units" ? Number(value) : value;
    setnewAbility((prev) => ({ ...prev, [name]: finalValue }));
  };
  const URL = "http://localhost:3500/api/abilities/";
  const subjectsList = ["Math", "Science", "History", "Literature", "Music"];

  useEffect(() => {
    // Fetch teacher abilities from the server
    fetch(`${URL}${user.user_id}`)
      .then((response) => response.json())
      .then((data) => {
        setAbilities(data); // Update the state with the fetched data
      })
      .catch((error) => {
        // console.error("Error fetching teacher abilities:", error);
      });
  }, []);

  const saveAbility = async () => {
    console.log(newAbility);
    if (editingIndex === -1) {
      fetch(`${URL}${user.user_id}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newAbility),
      })
        .then((response) => {
          if (response.ok) {
            console.log("Ability added successfully on the server");
            setAbilities((prevabilities) => [...prevabilities, newAbility]);
            setShowModal(false);
          } else {
            console.error(
              "Error adding ability on the server:",
              response.statusText
            );
            // Handle duplicate entry error
            if (response.status === 409) {
              alert("This ability already exists.");
            } else {
              // Handle other server-side errors
              alert(
                "An error occurred while adding the ability. Please try again."
              );
            }
          }
        })
        .catch((error) => {
          console.error("Fetch error:", error);
          // Handle network error
          alert(
            "A network error occurred. Please check your internet connection and try again."
          );
        });
    } else {
      // Edit existing subject locally
      const updatedAbilities = [...abilities];
      updatedAbilities[editingIndex] = newAbility;
      setAbilities(updatedAbilities);
      console.log(newAbility);
      if (newAbility.lesson_ability_id) {
        // Send a request to the server to update the ability
        fetch(`${URL}ability/${newAbility.lesson_ability_id}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(newAbility),
        })
          .then((response) => {
            if (response.ok) {
              console.log("Ability updated successfully on the server");
              setShowModal(false);
            } else {
              console.error(
                "Error updating ability on the server:",
                response.statusText
              );
              // Handle error by showing feedback to the user or reverting changes in UI
              alert(
                "An error occurred while updating the ability. Please try again."
              );
            }
          })
          .catch((error) => {
            console.error("Fetch error:", error);
            // Handle network error
            alert(
              "A network error occurred. Please check your internet connection and try again."
            );
          })
          .finally(() => {
            // Reset states after both successful and failed update
            setnewAbility(defaultAbility);
            setEditingIndex(-1);
            setShowModal(false);
          });
      }
    }
  };

  const deleteAbility = (ability_id, index) => {
    // Send a DELETE request to the server
    fetch(`${URL}ability/${ability_id}`, {
      method: "DELETE",
    })
      .then((response) => {
        if (response.ok) {
          console.log("Ability deleted successfully on the server");
          // Remove ability from local state as well
          const newAbilities = [...abilities];
          newAbilities.splice(index, 1);
          setAbilities(newAbilities);
        } else if (response.status === 500) {
          alert("You still have existing lessons that contain this ability");
        } else {
          console.error(
            "Error deleting ability on the server:",
            response.statusText
          );
        }
      })
      .catch((error) => {
        console.error("Fetch error:", error);
      });
  };

  const openEditModal = (index) => {
    setnewAbility(abilities[index]);
    setEditingIndex(index);
    setShowModal(true);
  };

  return (
    <div className="table-container">
      <h1 className="table-title">Teacher Abilities</h1>
      <table>
        <thead>
          <tr className="table-header">
            <th>Subject</th>
            <th>Level</th>
            <th>Units</th>
            <th>Cost</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {abilities.map((ability, index) => {
            return (
              <tr className="table-row" key={index}>
                <td>{ability.subject_name}</td>
                <td>{ability.type}</td>
                <td>{ability.units}</td>
                <td>{ability.cost}</td>
                <td>
                  <button
                    className="table-button"
                    onClick={() => openEditModal(index)}
                  >
                    Edit
                  </button>
                  <button
                    className="table-button"
                    onClick={() =>
                      deleteAbility(ability.lesson_ability_id, index)
                    }
                  >
                    Delete
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
      {/* Button to open the modal */}
      <button className="add-btn new-btn" onClick={() => setShowModal(true)}>
        Add New Ability
      </button>
      {/* Modal */}
      {showModal && (
        <div className="modal-ability">
          <div className="contact-us">
            <h2>Ability Details</h2>
            <div className="select-container">
              <label>
                <select
                  className="custom-dropdown"
                  name="subject_name"
                  value={newAbility.subject_name}
                  onChange={handleInputChange}
                  disabled={editingIndex !== -1}
                >
                  <option value="" disabled>
                    Select subject
                  </option>
                  {subjectsList.map((subject) => (
                    <option key={subject} value={subject}>
                      {subject}
                    </option>
                  ))}
                </select>
              </label>

              <label>
                <select
                  className="custom-dropdown"
                  name="type"
                  value={newAbility.type}
                  onChange={handleInputChange}
                  disabled={editingIndex !== -1}
                >
                  <option value="" disabled>
                    Select type
                  </option>
                  <option value={"School"}>{"School"}</option>
                  <option value={"High School"}>{"High School"}</option>
                  <option value={"University"}>{"University"}</option>
                </select>
              </label>
            </div>
            <div>
              <label>Units </label>
              <input
                className="text-input"
                name="units"
                type="number"
                min={0}
                value={newAbility.units}
                onChange={handleInputChange}
                disabled={editingIndex !== -1}
              />
            </div>
            <div>
              <label>Cost </label>
              <input
                className="text-input"
                name="cost"
                type="number"
                min={0}
                value={newAbility.cost}
                onChange={handleInputChange}
              />
            </div>
            <div className="details-btn-ctn">
              <button className="add-btn" onClick={saveAbility}>
                {editingIndex === -1 ? "Add Ability" : "Update Ability"}
              </button>
              <button
                className="add-btn"
                onClick={() => {
                  setEditingIndex(-1);
                  setShowModal(false);
                  setnewAbility(defaultAbility);
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
export default TeacherAbilities;
