import React, { useState } from "react";
import Vaccinations from "../vaccinations/vaccinations";

import EditIcon from "@mui/icons-material/Edit";
import IconButton from "@mui/material/IconButton";
import DeleteIcon from "@mui/icons-material/Delete";
import Button from "@mui/material/Button";
import CloseOutlinedIcon from "@mui/icons-material/CloseOutlined";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";

const NewPatientModal = ({
  isOpen,
  onClose,
  savePatient,
  newPatient,
  setNewPatient,
  deletePatient,
  isReadOnly,
}) => {
  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 600,
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 4,
  };

  // TODO - API for cities?
  const CitysList = [ {
    value: 'USD',
    label: '$',
  },
  {
    value: 'EUR',
    label: '€',
  },
  {
    value: 'BTC',
    label: '฿',
  },
  {
    value: 'JPY',
    label: '¥',
  },"Tel Aviv", "Jerusalem"];

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    const finalValue = value;
    setNewPatient((prev) => ({ ...prev, [name]: finalValue }));
  };

  const formattedDate = (date) => {
    return new Date(date).toLocaleDateString("en-US", {
      year: "numeric",
      month: "2-digit",
      day: "2-digit",
    });
  };

  const legalDate = (inputDate) => {
    const date = new Date(inputDate);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0"); // Months are 0-indexed, so we add 1
    const day = String(date.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  };

  if (!isOpen) return null;

  return (
    <Box sx={style}>
      <Box sx={{ display: "flex", flexWrap: "wrap" }}>
        <h2>{newPatient.name}</h2>
        <div>
          {isReadOnly ? (
            <label>{newPatient.id}</label>
          ) : (
            <TextField
              name="id"
              id="id"
              label="ID"
              type="text"
              value={newPatient.id}
              variant="outlined"
              onChange={handleInputChange}
              sx={{ m: 1, width: "25ch" }}
            />
          )}
        </div>
        <div>
          {isReadOnly ? (
            <label>{newPatient.name}</label>
          ) : (
            <TextField
              name="name"
              id="name"
              label="Name"
              type="text"
              value={newPatient.name}
              variant="outlined"
              onChange={handleInputChange}
              sx={{ m: 1, width: "25ch" }}
            />
          )}
        </div>
        <div>
          {isReadOnly ? (
            <label>{newPatient.city}</label>
          ) : (
            <TextField
              id="city"
              name="city"
              select
              label="city"
              value={newPatient.city}
              defaultValue="Jerusalem"
              helperText="Select your City"
              onChange={handleInputChange}
            >
              {CitysList.map((option) => (
                <MenuItem key={option.value} value={option.value}>
                  {option.label}
                </MenuItem>
              ))}
            </TextField>
          )}
        </div>
        <div>
          <label>Address</label>
          {isReadOnly ? (
            <label>{newPatient.address}</label>
          ) : (
            <input
              name="address"
              type="text"
              value={newPatient.address}
              onChange={handleInputChange}
            />
          )}
        </div>
        <div>
          <label>Birth Date</label>
          {isReadOnly ? (
            <label>{formattedDate(newPatient.birth_date)}</label>
          ) : (
            <input
              name="birth_date"
              type="date"
              value={legalDate(newPatient.birth_date)}
              onChange={handleInputChange}
            />
          )}
        </div>
        <div>
          <label>Phone</label>
          {isReadOnly ? (
            <label>{newPatient.phone}</label>
          ) : (
            <input
              name="phone"
              type="text"
              value={newPatient.phone}
              onChange={handleInputChange}
            />
          )}
        </div>
        <div>
          <label>Mobile Phone</label>
          {isReadOnly ? (
            <label>{newPatient.mobile_phone}</label>
          ) : (
            <input
              name="mobile_phone"
              type="text"
              value={newPatient.mobile_phone}
              onChange={handleInputChange}
            />
          )}
        </div>
        <div>
          <label>Positive result date</label>
          {isReadOnly ? (
            <label>{formattedDate(newPatient.positive_result_date)}</label>
          ) : (
            <input
              name="positive_result_date"
              type="date"
              value={
                newPatient.positive_result_date
                  ? legalDate(newPatient.positive_result_date)
                  : ""
              }
              onChange={handleInputChange}
            />
          )}
        </div>
        <div>
          <label>Recovery date</label>
          {isReadOnly ? (
            <label>{formattedDate(newPatient.recovery_date)}</label>
          ) : (
            <input
              name="recovery_date"
              type="date"
              value={legalDate(newPatient.recovery_date)}
              onChange={handleInputChange}
            />
          )}
        </div>
        <div>
          {newPatient && <Vaccinations patient={newPatient}></Vaccinations>}
        </div>
      </Box>
      <div>
        {!isReadOnly && (
          <IconButton aria-label="edit" color="primary">
            <EditIcon />
          </IconButton>
        )}
        {!isReadOnly && (
          <IconButton
            aria-label="delete"
            color="error"
            onClick={() => {
              deletePatient();
            }}
          >
            <DeleteIcon />
          </IconButton>
        )}
        <IconButton
          aria-label="cancel"
          color="primary"
          onClick={() => {
            onClose();
          }}
        >
          <CloseOutlinedIcon />
        </IconButton>
        <Button
          color="success"
          variant="outlined"
          onClick={() => {
            savePatient();
          }}
        >
          Save
        </Button>
      </div>
    </Box>
  );
};
export default NewPatientModal;
