import { useState, useEffect } from "react";
import { LineChart } from "@mui/x-charts/LineChart";

const BasicLineChart = () => {
  const URL = "http://localhost:3500/api/statistics/activePatients";
  const [XVals, setXVals] = useState(null);
  const [YVals, setYVals] = useState(null);

  const getLegalDates = (datesList) => {
    const newList = [];
    datesList.forEach((element) => {
      const date = new Date(element);
      newList.push(date.getDate());
    });
  };

  const getActivePatients = async () => {
    try {
      const response = await fetch(`${URL}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });
      if (response.ok) {
        const responseData = await response.json();
        setXVals(...responseData[0]);
        setYVals(getLegalDates(...responseData[1]));
     
      }
    } catch (error) {
      alert("Error fetching data");
    }
  };

  useEffect(() => {
    getActivePatients();
  }, []);
  return (
    <div>
      {XVals && YVals && (
        <LineChart
          xAxis={[{ data: XVals }]}
          series={[
            {
              data: YVals,
            },
          ]}
          width={500}
          height={300}
        />
      )}
    </div>
  );
};
export default BasicLineChart;
