import React, { useEffect, useState } from "react";

const History = () => {
  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Subject</th>
            <th>Level</th>
            <th>Units</th>
            <th>Date</th>
            <th>Time</th>
            {/* <th>Paid Up</th> */}
            {/* <th>Frontal</th>
            <th>Zoom</th> */}
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {Historylessons &&
            Historylessons.map((lesson, index) => (
              <tr key={lesson.lesson_id}>
                {user.user_type === "teacher" ? (
                  <td>{lesson.student_name}</td>
                ) : (
                  <td>{lesson.teacher_name}</td>
                )}
                {user.user_type === "teacher" ? (
                  <td>{lesson.student_phone}</td>
                ) : (
                  <td>{lesson.teacher_phone}</td>
                )}
                {user.user_type === "teacher" ? (
                  <td>
                    <a href={`mailto:${lesson.student_email}`}>
                      {lesson.student_email}
                    </a>
                  </td>
                ) : (
                  <td>
                    <a href={`mailto:${lesson.teacher_email}`}>
                      {lesson.teacher_email}
                    </a>
                  </td>
                )}
                <td>{lesson.subject}</td>
                <td>{lesson.subject_type}</td>
                <td>{lesson.subject_units}</td>
                <td>{new Date(lesson.b_time).toLocaleDateString()}</td>
                <td>
                  {new Date(lesson.b_time)
                    .toLocaleTimeString()
                    .split(":")
                    .slice(0, 2)
                    .join(":")}
                  -
                  {new Date(lesson.e_time)
                    .toLocaleTimeString()
                    .split(":")
                    .slice(0, 2)
                    .join(":")}
                </td>

                {/* {lesson.paid_up ? <td>V</td> : <td></td>} */}
                {/* {lesson.frontal ? <td>V</td> : <td></td>}
                {lesson.zoom ? <td>V</td> : <td></td>}
                 */}
                <td>{lesson.status}</td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
};

export default History;
