import React from "react";

const NewPatientModal = ({
  isOpen,
  onClose,
  isReadOnly,
  savePatient,
  newPatient,
  setNewPatient,
}) => {
  // TODO API for cities?
  const sitysList = [];

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    const finalValue = name === "units" ? Number(value) : value;
    setNewPatient((prev) => ({ ...prev, [name]: finalValue }));
  };

  if (!isOpen) return null;

  return (
    <div>
      <div>
        <h2>{newPatient.name}</h2>
        <div>
          <label>Name</label>
          <input
            name="name"
            type="text"
            value={newPatient.name}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>
            <select
              name="city"
              value={newPatient.city}
              onChange={handleInputChange}
            >
              <option value="" disabled>
                Select Sity
              </option>
              {sitysList.map((subject) => (
                <option key={subject} value={subject}>
                  {subject}
                </option>
              ))}
            </select>
          </label>
        </div>
        <div>
          <label>Address</label>
          <input
            name="address"
            type="text"
            value={newPatient.address}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Birth Date</label>
          <input
            name="birth date"
            type="date"
            value={newPatient.birth_date}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Phone</label>
          <input
            name="phone"
            type="text"
            value={newPatient.phone}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Mobile Phone</label>
          <input
            name="mobile phone"
            type="text"
            value={newPatient.mobile_phone}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Positive result date</label>
          <input
            name="Positive result date"
            type="date"
            value={newPatient.positive_result_date}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Recovery date</label>
          <input
            name="Recovery date"
            type="date"
            value={newPatient.recovery_date}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <button onClick={savePatient}>Save</button>
          <button onClick={() => {}}>Edit</button>
          <button
            onClick={() => {
              onClose();
            }}
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};
export default NewPatientModal;
