import React, { useState, useEffect } from 'react';

const LiveStatusIndicator = ({ isLive }) => (
    <div style={{
        height: '20px',
        width: '20px',
        backgroundColor: isLive ? 'green' : 'red',
        borderRadius: '50%',
        display: 'inline-block',
    }} />
);

const StartLive = ({ userDEtails, subjects }) => {

    const [user] = useState(userDEtails);
    const [selectedSubjects, setSelectedSubjects] = useState(new Set());
    const [duration, setDuration] = useState(10); //minutes
    const [onLive, setOnLive] = useState(false);
    const [timeLeft, setTimeLeft] = useState(0);//secondes
    const [checkLive, setcheckLive] = useState(false);
    const [lessonId, setLessonID] = useState(null);
    const formatTime = (time) => {
        const minutes = Math.floor(time / 60);
        const seconds = Math.round(time % 60);
        return `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
    };

    useEffect(() => {
        if (!checkLive) {
            getLive();
        }
    }, [])

    useEffect(() => {
        if (timeLeft <= 0) {
            setOnLive(false)
            console.log("time over");
            return;
        }
        const intervalId = setInterval(() => {
            setTimeLeft((prevTime) => prevTime - 1);
        }, 1000);
        return () => clearInterval(intervalId);

    }, [timeLeft]);

    const handleSubjectChange = (subjectId) => {
        setSelectedSubjects((prevSelectedSubjects) => {
            const newSelectedSubjects = new Set(prevSelectedSubjects);
            if (newSelectedSubjects.has(subjectId)) {
                newSelectedSubjects.delete(subjectId);
            } else {
                newSelectedSubjects.add(subjectId);
            }
            return newSelectedSubjects;
        });
    };

    const addLive = () => {
        const start_time = new Date();
        const end_time = new Date();
        end_time.setMinutes(start_time.getMinutes() + duration);
        const subjects = Array.from(selectedSubjects);

        // Create the request body
        const data = {
            teacher_id: user.user_id,
            start_time: start_time.toLocaleString("sv"),
            end_time: end_time.toLocaleString("sv"),
            subject_ids: subjects
        };
        console.log(data);

        // Send the POST request
        fetch('http://localhost:3500/api/lessons/live/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error(response.statusText);
                }
                return response.json();
            })
            .then(data => {
                console.log('Live lesson created successfully with ID: ' + data.lessonId);
                setOnLive(!onLive);
                setTimeLeft(duration * 60);
                setLessonID(data.lessonId);
            })
            .catch(error => {
                console.error('There has been a problem with your fetch operation:', error, error.message);
            });
    };

    const getLive = () => {
        fetch(`http://localhost:3500/api/lessons/live/current/${user.user_id}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                console.log('Current Live Lesson Details:', data);
                if (data.length > 0) {
                    setSelectedSubjects(new Set(
                        data.map((lesson) => {
                            return lesson.subject_id;
                        })));

                    setOnLive(true);
                    const left = (new Date(data[0].end_time) - new Date()) / 1000;
                    setTimeLeft(left)
                    setcheckLive(true);
                    setLessonID(data[0].live_lesson_id);
                }
            })
            .catch(error => {
                console.error('error:', error.message);
                setOnLive(false);
            });
    }
    const updateLive = (time) => {

        if (lessonId !== null) {
            let end_time = new Date();
            if (time!==0) {
                end_time.setSeconds(end_time.getSeconds() + timeLeft + duration * 60);
            }
            const data = {
                end_time: end_time.toLocaleString("sv"),
            };

            // Send the PUT request
            fetch(`http://localhost:3500/api/lessons/live/${lessonId}/`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(data)
            })
                .then(response => {
                    if (!response.ok) {
                        throw new Error('Network response was not ok ' + response.statusText);
                    }
                    return response.json();
                })
                .then(data => {
                    console.log('Live lesson updated successfully');
                    if (time===0) {
                        setOnLive(false);
                        setTimeLeft(0);
                        alert("Live lesson stop successfully");       
                    }
                    else {
                        setTimeLeft(timeLeft + duration * 60);
                        alert("Live lesson updated successfully");
                    }

                })
                .catch(error => {
                    console.error('Error', error, error.message);
                });
        }
    }

    const handleStart = (event) => {
        event.preventDefault(); 
        if (!onLive)//Start
        {
            if (selectedSubjects.size === 0) {
                alert("Please choose Subjects");
            }
            else {
                addLive();
            }
        }
        else //End
        {
            updateLive(0);
        }
    };
    return (
        <div>
            <LiveStatusIndicator isLive={onLive} />
            <h2>Live Lesson</h2>
            {onLive && <div>
                Left Time {formatTime(timeLeft)}
            </div>}

            {subjects !== undefined && subjects.map((subject) => (
                <div key={subject.subject_id}>
                    <input
                        type="checkbox"
                        id={subject.subject_id}
                        checked={selectedSubjects.has(subject.subject_id)}
                        onChange={() => handleSubjectChange(subject.subject_id)}
                        disabled={onLive}
                    />
                    <label htmlFor={subject.subject_id}>{subject.lesson_type_text}</label>
                </div>
            ))}
            <div>
                <label htmlFor="duration">Duration (minutes): </label>
                <input type="number" min={10} id="duration" value={duration} onChange={(event) => setDuration(event.target.value)} />
            </div>
            <button onClick={handleStart}>{!onLive ? 'Start Live' : 'Stop Live'} </button>
            <button onClick={updateLive} disabled={!onLive}>Add Duration</button>
        </div>
    );
};

export default StartLive;
