import React, { useState } from "react";

const UserDetails = ({ userDetails, onUserUpdate }) => {
  const [user, setUser] = useState(userDetails);
  const [isEditing, setIsEditing] = useState(false);
  const editableFields = ["Address", "Name", "Phone", "Birthdate"];

  const updateUser = (key, value) => {
    setUser((prev) => ({ ...prev, [key]: value }));
  };

  const areAllFieldsFilled = () => {
    for (let field of editableFields) {
      if (!user[field]) return false;
    }
    return true;
  };

  const formatDate = (dateString) => {
    if (!dateString) return "";
    try {
      var date = new Date(dateString);
      const year = date.getFullYear();
      const month = (date.getMonth() + 1).toString().padStart(2, "0"); // Months are 0-based in JavaScript
      const day = date.getDate().toString().padStart(2, "0");
      return `${year}-${month}-${day}`;
    } catch (error) {
      // console.error("Error formatting date:", error);
      return "";
    }
  };

  const saveEdit = () => {
    if (!areAllFieldsFilled()) {
      alert("Please fill all fields before saving.");
      return;
    }

    let birthdate = user.Birthdate;
    if (birthdate.length > 8) {
      birthdate = formatDate(birthdate);
    }
    fetch(
      `https://privatelessonsserver.azurewebsites.net/api/users/${user.user_id}`,
      {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name: user.Name,
          birthdate: birthdate.toString().slice(0, 10),
          address: user.Address,
          phone: user.Phone,
          gender: user.Gender,
        }),
      }
    )
      .then((response) => response.json())
      .then((data) => {
        onUserUpdate(user);
        setIsEditing(false); // Exit editing mode
      })
      .catch((error) => {
        // console.error("Error:", error);
      });
  };

  return (
    <div>
      <h1>{user.user_type === "student" ? "Hello Student" : "Hello Teacher"}</h1>
      <div>
        <div>
          <label>Name:</label>
          {isEditing ? (
            <input
              value={user.Name}
              onChange={(e) => updateUser("Name", e.target.value)}
            />
          ) : (
            <span >{user.Name}</span>
          )}
        </div>
        <div>
          <label >Birthday:</label>
          {isEditing ? (
            <input
              type="date"
              value={formatDate(user.Birthdate)}
              onChange={(e) => updateUser("Birthdate", e.target.value)}
            />
          ) : (
            <span >{formatDate(user.Birthdate)}</span>
          )}
        </div>
        <div>
          <label >Phone:</label>
          {isEditing ? (
            <input
              type="tel"
              pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
              value={user.Phone}
              onChange={(e) => updateUser("Phone", e.target.value)}
            />
          ) : (
            <span>{user.Phone}</span>
          )}
        </div>
        <div>
          <label>Email:</label>
          {isEditing ? (
            <input
              type="email"
              value={user.Email}
              onChange={(e) => updateUser("Email", e.target.value)}
            />
          ) : (
            <span>{user.Email}</span>
          )}
        </div>
        <div>
          <label>Address:</label>
          {isEditing ? (
            <input
              value={user.Address}
              onChange={(e) => updateUser("Address", e.target.value)}
            />
          ) : (
            <span>{user.Address}</span>
          )}
        </div>
        <br />
        {!isEditing ? (
          <div >
            <button onClick={() => setIsEditing(true)}>
              Edit
            </button>
          </div>
        ) : (
          <div>
            <button
              onClick={() => {
                saveEdit();
              }}
            >
              Save
            </button>
            <button
              onClick={() => {
                setIsEditing(false);
                setUser(userDetails);
              }}
            >
              Cancel
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserDetails;
