// Lessons.js
import React, { useEffect, useState } from "react";
import DetailsTable from "./DetailsTable/detailsTable";

const Lessons = ({ userDetails }) => {
  const [user] = useState(userDetails);
  const [lessons, setLessons] = useState(null);
  const [Historylessons, setHistorylessons] = useState(null);

  const handleLessonStatusUpdate = async (lessonIndex, status, lesson) => {
    // update lesson in server, then ger auto real-time update to the list.
    try {
      const response = await fetch(
        `https://privatelessonsserver.azurewebsites.net/api/lessons/${lesson.lesson_id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ paid_up: lesson.paid_up, status: status }),
        }
      );
      if (!response.ok) {
        // console.log("lesson update fails");
      } else {
        if (response.status === 200) {
          // console.log(`lesson with lesson_id: ${lesson.lesson_id} update`);
        }
      }
    } catch (error) {
      // console.log("ERROR", error);
    }

    // Clone the current lessons array
    const updatedLessons = [...lessons];

    // Update the status of the specific lesson
    updatedLessons[lessonIndex].status = status;

    // Set the updated lessons array as the new state
    setLessons(updatedLessons);
  };

  const getUserLessons = async () => {
    try {
      const response = await fetch(
        `https://privatelessonsserver.azurewebsites.net/api/lessons/optional/${user.user_id}/${user.user_type}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      if (response.ok) {
        const responseData = await response.json();
        if (responseData.length > 0) {
          setLessons(responseData);
        }
      } else {
        // console.log("Failed to load userLessons");
      }
    } catch (error) {
      // console.error("Error fetching data:", error);
    }
  };

  const getUserHistory = async () => {
    try {
      const response = await fetch(
        `https://privatelessonsserver.azurewebsites.net/api/lessons/history/${user.user_id}/${user.user_type}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      if (response.ok) {
        const responseData = await response.json();
        if (responseData.length > 0) {
          setHistorylessons(responseData);
        }
      } else {
        // console.log("Failed to load userHistory");
      }
    } catch (error) {
      // console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    if (lessons === null) {
      getUserLessons();
    }
    if (Historylessons === null) {
      getUserHistory();
    }
  }, []);

  return (
    <div>
      <h1>Lessons</h1>
      <DetailsTable></DetailsTable>
      <h1>History</h1>
    </div>
  );
};
export default Lessons;
