import React, { useState, useEffect } from "react";
import { googleLogout, useGoogleLogin } from "@react-oauth/google";
import { useNavigate } from "react-router-dom";
import { GoogleLogin } from "@react-oauth/google";

export default function Login() {
  const [user, setUser] = useState(null);
  const [connectedFails, setConnectedFails] = useState(false);
  const navigate = useNavigate();

  async function getUserInfo() {
    const localUser = JSON.parse(localStorage.getItem("connected_user"));
    if (localUser != null) {
      // If user connected try to get full info from server
      //TODO: Add timout
      try {
        const response = await fetch(
          `https://privatelessonsserver.azurewebsites.net/api/users/${localUser.user_id}`,
          {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
            },
          }
        );
        if (!response.ok) {
          setConnectedFails(true);
        } else {
          if (response.status === 200) {
            const user = await response.json();
            if (user) {
              //create user with full data in localStoarge
              localStorage.setItem("connected_user", JSON.stringify(user));
              setUser(user);
              //In case of exist user
              user.user_type === "" || user.user_type === null
                ? navigate(`/choseAccount/${user.user_id}`)
                : navigate(`/${user.user_type}/${user.user_id}`);
            }
          }
        }
      } catch (error) {
        // console.log("ERROR", error);
      }
    }
  }

  useEffect(() => {
    getUserInfo();
  }, []);

  const createSession = async (JWT_token) => {
    // google login
    try {
      const response = await fetch(
        `https://privatelessonsserver.azurewebsites.net/api/users/googleLogin/${JWT_token}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      if (!response.ok) {
        setConnectedFails(true);
      } else {
        if (response.status === 200) {
          const { user } = await response.json();
          //create user in localStoarge
          localStorage.setItem("connected_user", JSON.stringify(user));
          setUser(user);
          // exist user
          user.user_type === "" || user.user_type === null
            ? navigate(`/choseAccount/${user.user_id}`)
            : navigate(`/${user.user_type}/${user.user_id}`);
        } else if (response.status === 201) {
          const { user } = await response.json();
          localStorage.setItem("connected_user", JSON.stringify(user));
          setUser(user);
          // new user
          navigate(`/choseAccount/${user.user_id}`);
        }
      }
    } catch (error) {
      // console.log("ERROR", error);
    }
  };

  const login = (token) => {
    // request to server - create session and then navigat to details screen
    createSession(token);
  };

  return (
    <div className="container">
      <div className="top-right-login">
        <h2>Sign/Log In</h2>
        <div className="login-button">
          <GoogleLogin
            onSuccess={(credentialResponse) => {
              login(credentialResponse.credential);
            }}
            shape="circle"
            onError={() => {
              // console.log("Login Failed");
            }}
          />
          {connectedFails && (
            <h3 className="error-message">
              User connection failed, please try again.
            </h3>
          )}
        </div>
      </div>
      <h1>F(x) </h1>
      <h2>Private Lessons center</h2>
      <h3>Unlock the secrets of mathematics with our specialized courses.</h3>

      <div className="media-section">
        <img
          src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTsA0injQVaDOgOuEUFbm43VNAKpv65Wkcd5A&usqp=CAU"
          alt="Math Concept"
          className="math-image"
        />
        <img
          src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQrZmBMQLR0ZIQck7-G3JRQf_UKlOthTtgsJA&usqp=CAU"
          alt="Science Concept"
          className="science-image"
        />
        <img
          src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS00cBUdAWLoibekjpw6OCTaJOiGSG_bIyHGA&usqp=CAU"
          alt="Computer Science Concept"
          className="cs-image"
        />
        <iframe
          className="math-video"
          width="560"
          height="315"
          src="https://www.youtube.com/embed/wBy_H-5AqLM?si=ErJQ1bedPeKaiu2y"
          title="YouTube video player"
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
          allowFullScreen
        ></iframe>
      </div>

      <section className="features-section">
        <h2>Why Choose F(x) Lessons?</h2>
        <div className="feature">
          <h3>Personalized Lessons</h3>
          <p>
            Each lesson is tailored to fit the individual needs of our students.
          </p>
        </div>
        <div className="feature">
          <h3>Qualified Tutors</h3>
          <p>
            Our tutors are industry professionals with years of teaching
            experience.
          </p>
        </div>
        <div className="feature">
          <h3>Interactive Materials</h3>
          <p>
            We offer interactive exercises and quizzes to reinforce learning.
          </p>
        </div>
      </section>

      <section className="testimonials-section">
        <h2>What Our Students Say</h2>
        <div className="testimonial">
          <p>
            "I've learned so much in such a short amount of time. Highly
            recommend!"
          </p>
          <span>- Jane Doe</span>
        </div>
        <div className="testimonial">
          <p>"The tutors are extremely helpful and the lessons are fun!"</p>
          <span>- John Smith</span>
        </div>
      </section>
    </div>
  );
}
