import React, { useEffect, useState, useRef, useCallback } from "react";
import "devextreme/dist/css/dx.light.css";
import Scheduler, { Editing, Resource } from "devextreme-react/scheduler";
import StartLive from "../Teacher/StartLive";
//const STATUSES = ["Pending", "Approved","Denied", "Done", "Cancelled", "Available"];

const STATUS_COLORS = {
  Available: "#09b4e8",
  Pending: "#f0fc7c",
  Approved: "#78eb8f",
  Done: "#097507",
  Denied: "grey",
  Cancelled: "red",
};
const statuses = [
  { id: "Available", color: STATUS_COLORS["Available"] },
  { id: "Approved", color: STATUS_COLORS["Approved"] },
  { id: "Pending", color: STATUS_COLORS["Pending"] },
  { id: "Done", color: STATUS_COLORS["Done"] },
  { id: "Cancelled", color: STATUS_COLORS["Cancelled"] },
  { id: "Denied", color: STATUS_COLORS["Denied"] },
];

const Legend = () => {
  return (
    <div style={{ padding: "10px" }}>
      {/* <h4>Legend</h4> */}
      {Object.entries(STATUS_COLORS).map(([status, color]) => (
        <div
          key={status}
          style={{ display: "flex", alignItems: "center", marginBottom: "5px" }}
        >
          <div
            style={{
              width: "20px",
              height: "20px",
              backgroundColor: color,
              marginRight: "10px",
            }}
          ></div>
          <span>{status}</span>
        </div>
      ))}
    </div>
  );
};

const UserCalendar = ({ userDetails }) => {
  const [abilities, setAbilities] = useState([]);
  const scheduler = useRef(null);
  const [appointments, setAppointments] = useState([]);
  const [lessonInitialLoadComplete, setlessonInitialLoadComplete] =
    useState(false);
  const [
    lessonAbilityInitialLoadComplete,
    setlessonAbilityInitialLoadComplete,
  ] = useState(false);
  const [timeAbilityInitialLoadComplete, settimeAbilityInitialLoadComplete] =
    useState(false);
  const [user] = useState(userDetails);

  const isReadOnly = user.user_type === "student";

  const appointmentRender = (model) => {
    const { appointmentData } = model;
    return (
      <div>
        {user.user_type !== "student" &&
          appointmentData.status &&
          appointmentData.status !== "Available" && (
            <span>
              <strong>Student name:</strong> {appointmentData.student_name}{" "}
            </span>
          )}
        {user.user_type !== "teacher" && (
          <span>
            <strong>Teacher name:</strong> {appointmentData.teacher_name}{" "}
          </span>
        )}

        <span>
          <strong>Subject:</strong> {appointmentData.lesson_type_text}
        </span>
      </div>
    );
  };

  const fetchLessonsData = async () => {
    try {
      const response = await fetch(
        `http://localhost:3500/api/lessons/actual/${user.user_id}/${user.user_type}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      // Update the 'items' state with the fetched data.
      if (response.status === 200) {
        const data = await response.json();
        const currentDate = new Date();

        const newAppointments = data.map((appointment) => {
          let method = appointment.frontal === 1 ? "frontal" : "zoom";
          if (appointment.frontal === 1 && appointment.zoom === 1) {
            method = "frontal/zoom";
          }
          const formattedAppointment = {
            student_name: appointment.student_name,
            teacher_name: appointment.teacher_name,
            student_phone: appointment.student_phone,
            teacher_phone: appointment.teacher_phone,
            student_email: appointment.student_email,
            teacher_email: appointment.teacher_email,
            subject: appointment.subject,
            startDate: appointment.b_time,
            endDate: appointment.e_time,
            isEditable: new Date(appointment.b_time) < currentDate,
            status: appointment.status,
            id: `l${appointment.lesson_id}`,
            paid: appointment.paid_up,
            cost: appointment.lesson_cost,
            type: method,
            subject_type: appointment.subject_type,
            subject_units: appointment.subject_units,
            lesson_type_text: `${appointment.subject} ${appointment.subject_units} Units (${appointment.subject_type})`,
            lesson_ability_id: appointment.lesson_ability_id,
          };
          return formattedAppointment;
        });
        setAppointments((prevAppointments) => [
          ...prevAppointments,
          ...newAppointments,
        ]);
        setlessonInitialLoadComplete(true);
      } else {
        // console.error("Error fetching data:");
      }
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };
  const fetchLessonAbilities = async () => {
    // Fetch teacher abilities from the server
    fetch(`http://localhost:3500/api/abilities/${user.user_id}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.length) {
          setAbilities(
            data.map((ability) => {
              return {
                lesson_ability_id: ability.lesson_ability_id,
                cost: ability.cost,
                lesson_type_text: `${ability.subject_name} ${ability.units} Units (${ability.type})`,
                subject: ability.subject_name,
                units: ability.units,
                type: ability.type,
                subject_id: ability.subject_id,
              };
            })
          ); // Update the state with the fetched data

          if (abilities.length) {
            setlessonAbilityInitialLoadComplete(true);
          }
        } else {
          alert("Please enter Lesson Abilities!");
        }
      })
      .catch((error) => {
        // console.error("Error fetching teacher abilities:", error);
      });
  };

  const fetchTimeAbilities = async () => {
    try {
      const response = await fetch(
        `http://localhost:3500/api/timeAbilities/${user.user_id}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      if (response.ok) {
        const responseData = await response.json();
        if (responseData.length > 0) {
          // Use length to check if the array is not empty
          responseData.forEach((element) => {
            let method = element.frontal === 1 ? "frontal" : "zoom";
            if (element.frontal === 1 && element.zoom === 1) {
              method = "frontal/zoom";
            }
            element.status = "Available";
            element.startDate = element.start_time;
            element.endDate = element.end_time;
            element.id = `t${element.time_ability_id}`;
            element.type = method;
            element.cost = element.lesson_cost;
            element.isEditable = true;
            element.lesson_type_text = `${element.subject} ${element.units} Units (${element.subject_type})`;
          });
          setAppointments((prevAppointments) => [
            ...prevAppointments,
            ...responseData,
          ]);
        }
        settimeAbilityInitialLoadComplete(true);
      } else {
        // console.log("Failed to load teacherOptions");
      }
    } catch (error) {
      // console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    if (!lessonInitialLoadComplete) {
      fetchLessonsData();
    }
    if (!timeAbilityInitialLoadComplete && user.user_type !== "student") {
      fetchTimeAbilities();
    }
    if (!lessonAbilityInitialLoadComplete && user.user_type !== "student") {
      fetchLessonAbilities();
    }
  }, []);

  const doesOverlap = useCallback(
    (appointment1, appointment2, newAppointment = false) => {
      return (
        (newAppointment || appointment1.id !== appointment2.id) &&
        new Date(appointment1.startDate) < new Date(appointment2.endDate) &&
        new Date(appointment2.startDate) < new Date(appointment1.endDate)
      );
    },
    []
  );

  const onAppointmentFormOpening = useCallback(
    (e) => {
      const form = e.form;
      let formItems = form.option("items");

      e.popup.option("showTitle", true);
      e.popup.option(
        "title",
        e.appointmentData.lesson_type_text
          ? e.appointmentData.lesson_type_text
          : "Appointment Details"
      );
      // Remove the repeat button or recurrence editor by filtering out the item
      formItems = formItems.filter((item) => item.name !== "recurrenceGroup");
      formItems[0].items.forEach((group) => {
        if (group.items) {
          group.items = group.items.filter(
            (item) => item.dataField !== "repeat" && item.dataField !== "allDay"
          );
        }
      });
      formItems[0].items.forEach((group) => {
        if (group.itemType === "empty") {
          formItems[0].items.pop(group);
        }
      });
      formItems[0].items.forEach((group) => {
        if (group.itemType === "empty") {
          formItems[0].items.pop(group);
        }
      });
      formItems[0].items = formItems[0].items.filter(
        (item) => item.dataField !== "text" && item.dataField !== "description"
      );

      if (!e.appointmentData.id) {
        e.appointmentData.isEditable = true;
        e.appointmentData.status = "Available";
        e.appointmentData.paid = false;
        e.appointmentData.cost = 0;
        e.appointmentData.type = "zoom";
        e.appointmentData.student_name = "";
        e.appointmentData.teacher_name = "";
        e.lesson_type_text = "";
        e.appointmentData.id = 1;
      }

      formItems[0].items = formItems[0].items.filter(
        (item) =>
          item.dataField !== "student_name" &&
          item.dataField !== "teacher_name" &&
          item.dataField !== "teacher_phone" &&
          item.dataField !== "teacher_email" &&
          item.dataField !== "student_phone" &&
          item.dataField !== "student_email"
      );

      if (e.appointmentData.status !== "Available") {
        if (user.user_type !== "student") {
          formItems[0].items.push({
            colSpan: 1,
            label: { text: "Student Name" },
            editorType: "dxTextBox",
            dataField: "student_name",
            editorOptions: {
              readOnly: true,
              value: e.appointmentData.student_name,
            },
          });
          formItems[0].items.push({
            colSpan: 1,
            label: { text: "Student Phone" },
            editorType: "dxTextBox",
            dataField: "student_phone",
            editorOptions: {
              readOnly: true,
              value: e.appointmentData.student_phone,
            },
          });
          formItems[0].items.push({
            colSpan: 1,
            label: { text: "Student Email" },
            editorType: "dxTextBox",
            dataField: "student_email",
            editorOptions: {
              readOnly: true,
              value: e.appointmentData.student_email,
            },
          });
        }
        if (user.user_type !== "teacher") {
          formItems[0].items.push({
            colSpan: 1,
            label: { text: "Teacher Name" },
            editorType: "dxTextBox",
            dataField: "teacher_name",
            editorOptions: {
              readOnly: true,
              value: e.appointmentData.teacher_name,
            },
          });
          formItems[0].items.push({
            colSpan: 1,
            label: { text: "Teacher Phone" },
            editorType: "dxTextBox",
            dataField: "teacher_phone",
            editorOptions: {
              readOnly: true,
              value: e.appointmentData.teacher_phone,
            },
          });
          formItems[0].items.push({
            colSpan: 1,
            label: { text: "Teacher Email" },
            editorType: "dxTextBox",
            dataField: "teacher_email",
            editorOptions: {
              readOnly: true,
              value: e.appointmentData.teacher_email,
            },
          });
        }
      }

      formItems[0].items = formItems[0].items.filter(
        (item) => item.dataField !== "status"
      );
      formItems[0].items.push({
        colSpan: 1,
        label: { text: "Status" },
        editorType: "dxTextBox",
        dataField: "status",
        editorOptions: {
          readOnly: true,
          value: e.appointmentData.status,
        },
      });

      formItems[0].items = formItems[0].items.filter(
        (item) => item.dataField !== "cost"
      );
      formItems[0].items.push({
        colSpan: 1,
        label: { text: "Cost" },
        editorType: "dxNumberBox",
        dataField: "cost",
        editorOptions: {
          value: e.appointmentData.cost,
          readOnly: true,
        },
      });
      formItems[0].items = formItems[0].items.filter(
        (item) => item.dataField !== "type"
      );
      formItems[0].items.unshift({
        colSpan: 1,
        editorType: "dxSelectBox",
        dataField: "type",
        editorOptions: {
          items: ["zoom", "frontal", "frontal/zoom"],
          value: e.appointmentData.type,
        },
      });

      formItems[0].items = formItems[0].items.filter(
        (item) => item.dataField !== "lesson_ability_id"
      );
      if (user.user_type !== "student") {
        formItems[0].items.unshift({
          colSpan: 1,
          label: { text: "Lesson" },
          editorType: "dxSelectBox",
          dataField: "lesson_ability_id",
          editorOptions: {
            items: abilities,
            displayExpr: "lesson_type_text",
            valueExpr: "lesson_ability_id",
            onValueChanged: (args) => {
              const selectedLesson = abilities.find(
                (lesson) => lesson.lesson_ability_id === args.value
              );

              if (selectedLesson) {
                e.appointmentData.cost = selectedLesson.cost;
                e.appointmentData.lesson_type_text =
                  selectedLesson.lesson_type_text;
                // Directly update the cost editor's value without repainting the entire form
                const costEditor = form.getEditor("cost");
                if (costEditor) {
                  costEditor.option("value", selectedLesson.cost);
                }
              }
            },
          },
        });
      } else {
        formItems[0].items.unshift({
          colSpan: 1,
          label: { text: "Lesson" },
          editorType: "dxTextBox",
          dataField: "lesson_ability_id",
          editorOptions: {
            value: e.appointmentData.lesson_type_text,
          },
        });
      }

      formItems[0].items = formItems[0].items.filter(
        (item) => item.dataField !== "paid"
      );
      if (e.appointmentData.id && e.appointmentData.status !== "Available") {
        formItems[0].items.push({
          colSpan: 1,
          label: { text: "Paid Up" },
          editorType: "dxCheckBox",
          dataField: "paid",
          editorOptions: {
            readOnly:
              (e.appointmentData.status === "Approved" ||
                e.appointmentData.status === "Done") &&
              user.user_type === "teacher"
                ? false
                : true,
            value: e.appointmentData.paid,
          },
        });
      }
      form.option("items", formItems);

      const updateEditorOptions = (items) => {
        items.forEach((item) => {
          if (item.items) {
            // Recursively handle groups
            updateEditorOptions(item.items);
          } else if (
            item.dataField &&
            item.dataField !== "status" &&
            item.dataField !== "paid" &&
            item.dataField !== "cost"
          ) {
            const currentEditorOptions = item.editorOptions || {};
            item.editorOptions = {
              ...currentEditorOptions,
              readOnly: !e.appointmentData.isEditable,
            };
          }
        });
      };
      if (e.appointmentData.isEditable === false) {
        updateEditorOptions(formItems);
      }

      form.option("items", [...formItems]);
      form.repaint();

      if (user.user_type === "student") {
        const toolbarItems = e.popup.option("toolbarItems");
        toolbarItems.forEach((item) => {
          if (item.shortcut === "done") {
            item.options.visible = e.appointmentData.isEditable;
          }
        });
        e.popup.option("toolbarItems", toolbarItems);
        form.repaint();
      }

    },
    [user.user_type, abilities]
  );

  const onAppointmentAdding = useCallback(
    (e) => {
      e.cancel = true;
      const newAppointment = e.appointmentData;
      for (let appointment of appointments) {
        if (doesOverlap(newAppointment, appointment, true)) {
          e.cancel = true;
          alert("Appointment overlaps with an existing appointment!");
          return;
        }
      }
      if (newAppointment.type === "zoom") {
        newAppointment.zoom = true;
        newAppointment.frontal = false;
      } else if (newAppointment.type === "frontal") {
        newAppointment.zoom = false;
        newAppointment.frontal = true;
      } else if (newAppointment.type === "frontal/zoom") {
        newAppointment.zoom = true;
        newAppointment.frontal = true;
      }
      const selectedLesson = abilities.find(
        (lesson) =>
          lesson.lesson_ability_id === newAppointment.lesson_ability_id
      );
      newAppointment.status = "Available";
      newAppointment.cost = selectedLesson.cost;
      newAppointment.subject_name = selectedLesson.subject;
      newAppointment.subject_type = selectedLesson.type;
      newAppointment.subject_units = selectedLesson.units;
      newAppointment.lesson_type_text = selectedLesson.lesson_type_text;
      newAppointment.startDate = new Date(newAppointment.startDate)
        .toLocaleString("sv")
        .replace(" ", "T");
      newAppointment.endDate = new Date(newAppointment.endDate)
        .toLocaleString("sv")
        .replace(" ", "T");

      fetch(`http://localhost:3500/api/timeAbilities`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newAppointment),
      })
        .then((response) => {
          if (!response.ok) {
            e.cancel = true;
            throw new Error(`Request failed with status: ${response.status}`);
          }
          return response.json();
        })
        .then((data) => {
          // Access the insertId from the response
          newAppointment.id = `t${data.insertId}`;
          newAppointment.time_ability_id = `t${data.insertId}`;
          // Add the updated appointment to the state
          setAppointments((prevAppointments) => [
            ...prevAppointments,
            newAppointment,
          ]);
          // Cancel the original appointment
          e.cancel = true;
        })
        .catch((error) => {
          // Handle errors here
          // console.error("Error:", error.message);
          // You can display an error message to the user or perform error-specific actions
          e.cancel = true;
        });
    },
    [doesOverlap, appointments, abilities]
  );
  const setCancelAppointment = (appointmentId) => {
    setAppointments((prevAppointments) =>
      prevAppointments.map((appointment) => {
        if (appointment.id === appointmentId) {
          // Update the status to "Cancelled"
          return {
            ...appointment,
            status: "Cancelled",
          };
        }
        return appointment;
      })
    );
  };

  const onAppointmentDeleting = useCallback(
    async (e) => {
      e.cancel = true;
      if (e.appointmentData.status === "Done") {
        return;
      } else if (e.appointmentData.status !== "Available") {
        const userConfirmed = window.confirm(
          "Are you sure you want to cancel this lesson?"
        );
        if (!userConfirmed) {
          e.cancel = true;
          return;
        }
        try {
          // Send an HTTP DELETE request to the server to delete the lesson
          const response = await fetch(
            `http://localhost:3500/api/lessons/${e.appointmentData.id
              .toString()
              .slice(1)}`,
            {
              method: "DELETE",
              headers: {
                "Content-Type": "application/json",
              },
            }
          );

          if (response.status === 200) {
            // Lesson was successfully deleted
            alert(
              `Lesson ${e.appointmentData.lesson_type_text} has been cancelled.`
            );
            setCancelAppointment(e.appointmentData.id);
          } else if (response.status === 404) {
            // Lesson not found
            // console.log(`Lesson ${e.appointmentData.id} not found.`);
            e.cancel = true;
            return;
          } else {
            // Handle other error cases
            // console.error("An error occurred while cancelling the lesson.");
            e.cancel = true;
            return;
          }
        } catch (error) {
          // console.error("An error occurred:", error);
          e.cancel = true;
          return;
        }
      } else {
        fetch(
          `http://localhost:3500/api/timeAbilities/${e.appointmentData.id
            .toString()
            .slice(1)}`,
          {
            method: "DELETE",
          }
        )
          .then((response) => {
            if (response.status === 200) {
              // Time ability deleted successfully
              alert("Appointment deleted successfully.");
              setAppointments((prevAppointments) => {
                return prevAppointments.filter(
                  (a) =>
                    a.id.toString().slice(1) !==
                    e.appointmentData.id.toString().slice(1)
                );
              });
            } else if (response.status === 404) {
              // Time ability not found
              // console.log("Time ability not found.");
            } else if (response.status === 403) {
              // Time ability in use and cannot be deleted
              alert("Time ability is in use and cannot be deleted.");
              e.cancel = true;
            } else {
              // Handle other errors here
              alert("Time ability is in use and cannot be deleted.");
              // console.log("An error occurred while deleting the time ability.");
              e.cancel = true;
            }
          })
          .catch((error) => {
            // Handle network errors here
            // console.error("Network error:", error);
          });
      }
    },
    [isReadOnly]
  );

  const onAppointmentUpdating = useCallback(
    (e) => {
      if (isReadOnly) {
        e.cancel = true;
        return;
      }

      if (e.newData.status === "Available") {
        const selectedLesson = abilities.find(
          (lesson) => lesson.lesson_ability_id === e.newData.lesson_ability_id
        );
        e.newData.cost = selectedLesson.cost;
        e.newData.subject_name = selectedLesson.subject;
        e.newData.subject_type = selectedLesson.type;
        e.newData.subject_units = selectedLesson.units;
        e.newData.lesson_type_text = selectedLesson.lesson_type_text;
        for (let appointment of appointments) {
          if (doesOverlap(e.newData, appointment)) {
            e.cancel = true;
            alert("Appointment overlaps with an existing appointment!");
            return;
          }
        }
        const fullUpdatedAppointment = e.newData;
        if (fullUpdatedAppointment.isEditable === false) {
          e.cancel = true;
          return;
        }

        if (fullUpdatedAppointment.type === "zoom") {
          fullUpdatedAppointment.zoom = true;
          fullUpdatedAppointment.frontal = false;
        } else if (fullUpdatedAppointment.type === "frontal") {
          fullUpdatedAppointment.zoom = false;
          fullUpdatedAppointment.frontal = true;
        } else if (fullUpdatedAppointment.type === "frontal/zoom") {
          fullUpdatedAppointment.zoom = true;
          fullUpdatedAppointment.frontal = true;
        }
        fullUpdatedAppointment.startDate = new Date(
          fullUpdatedAppointment.startDate
        )
          .toLocaleString("sv")
          .replace(" ", "T");
        fullUpdatedAppointment.endDate = new Date(
          fullUpdatedAppointment.endDate
        )
          .toLocaleString("sv")
          .replace(" ", "T");

        fetch(
          `http://localhost:3500/api/timeAbilities/${fullUpdatedAppointment.id
            .toString()
            .slice(1)}`,
          {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(fullUpdatedAppointment),
          }
        )
          .then((response) => {
            if (!response.ok) {
              throw new Error(`Request failed with status: ${response.status}`);
            }
            return response.json();
          })
          .then((data) => {
            // Handle the successful response here
            // console.log("Success:", data.message);
          })
          .catch((error) => {
            // Handle errors here
            // console.error("Error:", error.message);
            e.cancel = true;
          });
      } else {
        if (
          e.newData.startDate !== e.oldData.startDate ||
          e.newData.endDate !== e.oldData.endDate
        ) {
          e.cancel = true;
          return;
        }
        for (let appointment of appointments) {
          if (doesOverlap(e.newData, appointment)) {
            e.cancel = true;
            alert("Appointment overlaps with an existing appointment!");
            return;
          }
        }
        const fullUpdatedAppointment = e.newData;
        // Make the PUT request using fetch
        return fetch(
          `http://localhost:3500/api/lessons/${fullUpdatedAppointment.id
            .toString()
            .slice(1)}`,
          {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              paid_up: fullUpdatedAppointment.paid,
              status: fullUpdatedAppointment.status,
            }),
          }
        )
          .then((response) => {
            if (!response.ok) {
              throw new Error(`Failed with status: ${response.status}`);
            }
            return response.text();
          })
          .then((data) => {
            // console.log(data);
          })
          .catch((error) => {
            // console.error("Error updating lesson:", error);
          });
      }
    },
    [doesOverlap, appointments, isReadOnly, abilities]
  );

  let views = ["day", "week", "month"];

  const handleAddAppointmentClick = () => {
    const appointmentData = {};
    scheduler.current.instance.showAppointmentPopup(appointmentData, true);
  };

  const onCellClick = useCallback(
    (e) => {
      if (isReadOnly || new Date(e.cellData.startDate) < new Date()) {
        e.cancel = true;
        return;
      }
    },
    [isReadOnly]
  );

  return (
    <div className="calendar-container">
      <div className="scheduler">
        <Scheduler
          ref={scheduler}
          dataSource={appointments}
          timeZone="Asia/Jerusalem"
          defaultCurrentDate={new Date()}
          defaultCurrentView="day"
          views={views}
          height={600}
          width={1300}
          firstDayOfWeek={0}
          startDayHour={7}
          endDayHour={24}
          showAllDayPanel={false}
          crossScrollingEnabled={true}
          cellDuration={60}
          appointmentRender={appointmentRender}
          onAppointmentFormOpening={onAppointmentFormOpening}
          onAppointmentAdding={onAppointmentAdding}
          onAppointmentUpdating={onAppointmentUpdating}
          onAppointmentDeleting={onAppointmentDeleting}
          onCellClick={onCellClick}
          // groups={groups}
        >
          <Resource fieldExpr="status" dataSource={statuses} />
        </Scheduler>
        <Editing allowAdding={!isReadOnly} allowDeleting={!isReadOnly} />
      </div>
      <div
        className="btn-ctn-sch"
        style={{
          width: "200px",
          padding: "10px",
          borderLeft: "1px solid #ccc",
        }}
      >
        <Legend className="legend" />
        {!isReadOnly && (
          <button className="add-btn" onClick={handleAddAppointmentClick}>
            Add Appointment
          </button>
        )}
        {!isReadOnly && (
          <StartLive userDEtails={user} subjects={abilities}></StartLive>
        )}
      </div>
    </div>
  );
};

export default UserCalendar;
