import React, { useState, useEffect } from "react";

import Menu from "../Menu/Menu";

const StudentPage = () => {
  const [activeTab, setActiveTab] = useState(1);
  const [user, setUser] = useState(null);
  const [initialLoadComplete, setInitialLoadComplete] = useState(false);

  const initialUser = function () {
    const connected_user = JSON.parse(localStorage.getItem("connected_user"));
    if (connected_user) {
      setInitialLoadComplete(true);
      setUser({
        user_id: connected_user.user_id,
        user_type: connected_user.user_type,
        Name: connected_user.name !== null ? connected_user.name : "",
        Gender: connected_user.gender !== null ? connected_user.gender : "",
        Email: connected_user.email !== null ? connected_user.email : "",
        Address: connected_user.address !== null ? connected_user.address : "",
        Birthdate:
          connected_user.birthdate !== null ? connected_user.birthdate : "",
        Phone: connected_user.phone !== null ? connected_user.phone : "",
      });
    } else {
      // console.log("user initial fails");
    }
  };

  useEffect(() => {
    if (initialLoadComplete === false) {
      initialUser();
    }
  }, []);

  return (
    <div>
      <Menu></Menu>
    </div>
  );
};

export default StudentPage;
